                  labelFormatter={(label) => label}
                />
                <Legend />
                <Area
                  type="monotone"
                  dataKey="projected"
                  name="Projected Revenue"
                  fill="#4F46E580"
                  stroke="#4F46E5"
                  strokeWidth={2}
                  connectNulls={true}
                />
                <Bar
                  dataKey="actual"
                  name="Actual Revenue"
                  fill="#10B981"
                  connectNulls={true}
                />
                {/* Add a reference line where actuals end */}
                {lastActualWeekIndex >= 0 && (
                  <ReferenceLine
                    x={`Week ${weeklyProjections[lastActualWeekIndex].week}`}
                    stroke="#888"
                    strokeDasharray="3 3"
