      { name: 'Mar', spend: 3500, conversions: 172, cac: 20.3 },
      { name: 'Apr', spend: 4100, conversions: 193, cac: 21.2 },
      { name: 'May', spend: 4800, conversions: 238, cac: 20.2 },
      { name: 'Jun', spend: 5200, conversions: 251, cac: 20.7 },
