                      ) : (
                        <>
                          <ChevronDown className="h-3 w-3 mr-1" />
                          Below Target
                        </>
                      )}
                    </Badge>
                    {acquisitionMetrics.dataSource !== 'none' && (
                      <Badge variant="outline" className="ml-2 text-xs">
                        {acquisitionMetrics.dataSource === 'actuals' ? 'From Actuals' : 
