              <TabsTrigger value="channels">Channel Analysis</TabsTrigger>
              <TabsTrigger value="funnel">Conversion Funnel</TabsTrigger>
              <TabsTrigger value="trends">Performance Trends</TabsTrigger>
            </TabsList>
            
            {/* Overview Tab */}
            <TabsContent value="overview">
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                <Card>
                  <CardHeader className="pb-0">
                    <CardTitle className="text-lg">Marketing Budget Allocation</CardTitle>
                  </CardHeader>
                  <CardContent>
                    {renderMarketingBudgetAllocation()}
                  </CardContent>
                </Card>
                
                <Card>
                  <CardHeader className="pb-0">
                    <CardTitle className="text-lg">Channel Effectiveness</CardTitle>
                  </CardHeader>
                  <CardContent>
                    {renderChannelEffectivenessTable()}
                  </CardContent>
                </Card>
                
                <Card className="lg:col-span-2">
                  <CardHeader className="pb-0">
                    <CardTitle className="text-lg">Acquisition Cost Trend</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="h-[300px]">
                      <ResponsiveContainer width="100%" height="100%">
                        <LineChart data={monthlyTrendData}>
                          <CartesianGrid strokeDasharray="3 3" />
                          <XAxis dataKey="name" />
                          <YAxis yAxisId="left" orientation="left" />
                          <YAxis yAxisId="right" orientation="right" />
                          <Tooltip content={CustomTooltip} />
                          <Legend />
                          <Line 
                            yAxisId="left"
                            type="monotone" 
                            dataKey="spend" 
                            name="Marketing Spend" 
                            stroke="#8884d8" 
                            activeDot={{ r: 8 }} 
                          />
                          <Line 
                            yAxisId="left"
                            type="monotone" 
                            dataKey="conversions" 
                            name="Conversions" 
                            stroke="#82ca9d" 
                          />
                          <Line 
                            yAxisId="right"
                            type="monotone" 
                            dataKey="cac" 
                            name="CAC" 
                            stroke="#ff7300" 
                          />
                        </LineChart>
                      </ResponsiveContainer>
                    </div>
                  </CardContent>
                </Card>
              </div>
            </TabsContent>
            
            {/* Channel Analysis Tab */}
            <TabsContent value="channels">
              {marketingData.hasChannels ? (
                <div className="space-y-6">
                  <Card>
                    <CardHeader className="pb-0">
                      <CardTitle className="text-lg">Channel Performance Comparison</CardTitle>
                    </CardHeader>
                    <CardContent>
                      <div className="h-[400px]">
                        <ResponsiveContainer width="100%" height="100%">
                          <BarChart 
                            data={channelComparisonData}
                            margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
                          >
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="name" />
                            <YAxis />
                            <Tooltip content={CustomTooltip} />
                            <Legend />
                            <Bar dataKey="Budget Allocation" fill="#8884d8" name="Budget %" unit="%" />
                            <Bar dataKey="Conversion Rate" fill="#82ca9d" name="Conv Rate" unit="%" />
                            <Bar dataKey="ROI" fill="#ffc658" name="ROI" unit="%" />
                            <Bar dataKey="CTR" fill="#ff8042" name="CTR" unit="%" />
                          </BarChart>
                        </ResponsiveContainer>
                      </div>
                    </CardContent>
                  </Card>
                  
                  <Card>
                    <CardHeader className="pb-0">
                      <CardTitle className="text-lg">Channel ROI Analysis</CardTitle>
                    </CardHeader>
                    <CardContent>
                      <Table>
                        <TableHeader>
                          <TableRow>
                            <TableHead>Channel</TableHead>
                            <TableHead>Weekly Budget</TableHead>
                            <TableHead>Est. Monthly Spend</TableHead>
                            <TableHead>Est. Conversions</TableHead>
                            <TableHead>Avg. CAC</TableHead>
                            <TableHead>Est. ROI</TableHead>
                            <TableHead>Recommendation</TableHead>
                          </TableRow>
                        </TableHeader>
                        <TableBody>
                          {channelPerformance.map((channel) => {
                            const monthlySpend = channel.budget * 4;
                            const estConversions = Math.round((channel.conversionRate / 100) * (monthlySpend / 10)); // Rough estimation
                            const channelCac = estConversions > 0 ? monthlySpend / estConversions : 0;
                            
                            let recommendation;
                            if (channel.roi > 50) recommendation = "Increase budget";
                            else if (channel.roi < 0) recommendation = "Decrease budget";
                            else recommendation = "Maintain budget";
                            
                            return (
                              <TableRow key={channel.id}>
                                <TableCell className="font-medium">{channel.name}</TableCell>
                                <TableCell>{formatCurrency(channel.budget)}</TableCell>
                                <TableCell>{formatCurrency(monthlySpend)}</TableCell>
                                <TableCell>{formatNumber(estConversions)}</TableCell>
                                <TableCell>{formatCurrency(channelCac)}</TableCell>
                                <TableCell className={channel.roi < 0 ? 'text-red-500' : 'text-green-500'}>
                                  {channel.roi.toFixed(1)}%
                                </TableCell>
                                <TableCell>
                                  <Badge
                                    variant={
                                      recommendation === "Increase budget" 
                                        ? "default"
                                        : recommendation === "Decrease budget" 
                                          ? "destructive" 
                                          : "outline"
                                    }
                                  >
                                    {recommendation}
                                  </Badge>
                                </TableCell>
                              </TableRow>
                            );
                          })}
                        </TableBody>
                      </Table>
                      <div className="text-sm text-gray-500 italic text-center mt-4">
                        * Recommendations based on estimated performance data - should be validated with actual analytics
                      </div>
                    </CardContent>
                  </Card>
                </div>
              ) : (
                <div className="flex items-center justify-center h-[400px] text-center">
                  <div>
                    <p className="text-gray-500 mb-4">No marketing channels configured</p>
                    <Button 
                      variant="outline" 
                      onClick={() => window.location.href = '/cost-forecast'}
                    >
                      Configure in Cost Forecast
                      <ExternalLink className="h-4 w-4 ml-2" />
                    </Button>
                  </div>
                </div>
              )}
            </TabsContent>
            
            {/* Conversion Funnel Tab */}
            <TabsContent value="funnel">
              {renderConversionFunnel()}
            </TabsContent>
            
            {/* Performance Trends Tab */}
            <TabsContent value="trends">
              <Card>
                <CardHeader className="pb-0">
                  <CardTitle className="text-lg">Marketing Performance Metrics (6-Month Trend)</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="h-[400px]">
                    <ResponsiveContainer width="100%" height="100%">
                      <LineChart data={monthlyTrendData}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="name" />
                        <YAxis />
                        <Tooltip content={CustomTooltip} />
                        <Legend />
                        <Line type="monotone" dataKey="spend" name="Marketing Spend" stroke="#8884d8" />
                        <Line type="monotone" dataKey="conversions" name="Conversions" stroke="#82ca9d" />
                        <Line type="monotone" dataKey="cac" name="CAC" stroke="#ff7300" />
                      </LineChart>
                    </ResponsiveContainer>
                  </div>
                  
                  <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-4">
                    <div className="bg-gray-50 p-4 rounded-md">
                      <h4 className="font-medium text-sm text-gray-500 mb-1">Average CAC</h4>
                      <p className="text-xl font-bold">
                        {formatCurrency(
                          monthlyTrendData.reduce((sum, month) => sum + month.cac, 0) / monthlyTrendData.length
                        )}
                      </p>
                      <p className="text-sm text-gray-500 mt-1">Average cost to acquire one customer</p>
                    </div>
                    
                    <div className="bg-gray-50 p-4 rounded-md">
                      <h4 className="font-medium text-sm text-gray-500 mb-1">Monthly Spend Growth</h4>
                      <p className="text-xl font-bold">
                        {monthlyTrendData.length > 1 
                          ? `${(((monthlyTrendData[monthlyTrendData.length-1].spend / monthlyTrendData[0].spend) - 1) * 100).toFixed(1)}%` 
                          : '0%'
                        }
                      </p>
                      <p className="text-sm text-gray-500 mt-1">Growth in marketing spend over period</p>
                    </div>
                    
                    <div className="bg-gray-50 p-4 rounded-md">
                      <h4 className="font-medium text-sm text-gray-500 mb-1">Conversion Growth</h4>
                      <p className="text-xl font-bold">
                        {monthlyTrendData.length > 1 
                          ? `${(((monthlyTrendData[monthlyTrendData.length-1].conversions / monthlyTrendData[0].conversions) - 1) * 100).toFixed(1)}%` 
                          : '0%'
                        }
                      </p>
                      <p className="text-sm text-gray-500 mt-1">Growth in customer conversions over period</p>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
        </CardContent>
      </Card>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from './ui/card';
import { Button } from './ui/button';
import { Input } from './ui/input';
import { Label } from './ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from './ui/select';
import { Switch } from './ui/switch';
import { Tabs, TabsContent, TabsList, TabsTrigger } from './ui/tabs';
import { Alert, AlertDescription, AlertTitle } from './ui/alert';
import { Badge } from './ui/badge';
import { Loader2, Check, X, RefreshCw, Download, ExternalLink } from 'lucide-react';
import useStore from '../store/useStore';
import { 
  ApiConfig, 
  MarketingPlatform, 
  configureMarketingApi, 
  fetchMarketingData,
  getApiConfig,
  listConfiguredApis,
  saveApiConfigurations,
  transformApiDataToChannelPerformance
} from '../lib/marketingApi';
import { formatCurrency, formatNumber, formatPercent } from '../lib/utils';

export default function MarketingApiIntegration() {
  const { products, currentProductId, updateProduct } = useStore();
  const [activeTab, setActiveTab] = useState<MarketingPlatform>('facebook');
  const [isImporting, setIsImporting] = useState(false);
  const [importStatus, setImportStatus] = useState<'idle' | 'loading' | 'success' | 'error'>('idle');
  const [statusMessage, setStatusMessage] = useState('');
  const [startDate, setStartDate] = useState(
    new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]
  );
  const [endDate, setEndDate] = useState(
    new Date().toISOString().split('T')[0]
  );
  
  // API configuration state
  const [apiConfigs, setApiConfigs] = useState<Record<MarketingPlatform, ApiConfig | null>>({
    facebook: null,
    google: null,
    twitter: null,
    linkedin: null,
    tiktok: null,
    custom: null
  });
  
  // Form state for current platform
  const [formConfig, setFormConfig] = useState<ApiConfig>({
    platformName: '',
    apiKey: '',
    endpoint: '',
    isActive: true
  });
  
  // Load saved configurations on mount
  useEffect(() => {
    const platforms: MarketingPlatform[] = ['facebook', 'google', 'twitter', 'linkedin', 'tiktok', 'custom'];
    const configs: Record<MarketingPlatform, ApiConfig | null> = {
      facebook: null,
      google: null,
      twitter: null,
      linkedin: null,
      tiktok: null,
      custom: null
    };
    
    platforms.forEach(platform => {
      const config = getApiConfig(platform);
      if (config) {
        configs[platform] = config;
      }
    });
    
    setApiConfigs(configs);
    
    // Initialize form with active tab's config
    const activeConfig = getApiConfig(activeTab);
    if (activeConfig) {
      setFormConfig(activeConfig);
    } else {
      setFormConfig({
        platformName: getPlatformDisplayName(activeTab),
        apiKey: '',
        endpoint: getDefaultEndpoint(activeTab),
        isActive: true
      });
    }
  }, []);
  
  // Update form when tab changes
  useEffect(() => {
    const config = getApiConfig(activeTab);
    if (config) {
      setFormConfig(config);
    } else {
      setFormConfig({
        platformName: getPlatformDisplayName(activeTab),
        apiKey: '',
        endpoint: getDefaultEndpoint(activeTab),
        isActive: true
      });
    }
  }, [activeTab]);
  
  // Get current product
  const currentProduct = products.find(p => p.info.id === currentProductId);
  
  if (!currentProduct) {
    return (
      <div className="text-center py-8 text-gray-500">
        No product selected or product not found.
      </div>
    );
  }
  
  // Helper to get platform display name
  function getPlatformDisplayName(platform: MarketingPlatform): string {
    switch (platform) {
      case 'facebook': return 'Facebook Ads';
      case 'google': return 'Google Ads';
      case 'twitter': return 'Twitter Ads';
      case 'linkedin': return 'LinkedIn Ads';
      case 'tiktok': return 'TikTok Ads';
      case 'custom': return 'Custom API';
      default: return platform;
    }
  }
  
  // Helper to get default endpoint
  function getDefaultEndpoint(platform: MarketingPlatform): string {
    switch (platform) {
      case 'facebook': return 'https://graph.facebook.com/v16.0/';
      case 'google': return 'https://googleads.googleapis.com/v14/';
      case 'twitter': return 'https://ads-api.twitter.com/10/';
      case 'linkedin': return 'https://api.linkedin.com/v2/adAnalytics';
      case 'tiktok': return 'https://business-api.tiktok.com/open_api/v1.3/';
      case 'custom': return 'https://api.example.com/v1/';
      default: return '';
    }
  }
  
  // Handle form input changes
  const handleInputChange = (field: keyof ApiConfig, value: string | boolean) => {
    setFormConfig(prev => ({
      ...prev,
      [field]: value
    }));
  };
  
  // Save API configuration
  const saveApiConfig = () => {
    configureMarketingApi(activeTab, formConfig);
    saveApiConfigurations();
    
    // Update local state
    setApiConfigs(prev => ({
      ...prev,
      [activeTab]: formConfig
    }));
    
    setStatusMessage(`${getPlatformDisplayName(activeTab)} configuration saved successfully.`);
    setTimeout(() => setStatusMessage(''), 3000);
  };
  
  // Import data from API
  const importData = async () => {
    if (!currentProduct) return;
    
    setIsImporting(true);
    setImportStatus('loading');
    setStatusMessage(`Importing data from ${getPlatformDisplayName(activeTab)}...`);
    
    try {
      // Fetch data from API
      const response = await fetchMarketingData(activeTab, startDate, endDate);
      
      if (!response.success) {
        throw new Error(response.error || 'Failed to import data');
      }
      
      // Find matching marketing channel in the product
      const marketingChannels = currentProduct.costMetrics?.marketing?.channels || [];
      const matchingChannel = marketingChannels.find(
        channel => channel.name.toLowerCase().includes(activeTab.toLowerCase())
      );
      
      if (!matchingChannel) {
        throw new Error(`No matching marketing channel found for ${getPlatformDisplayName(activeTab)}`);
      }
      
      // Transform API data to our format
      const channelPerformance = transformApiDataToChannelPerformance(
        activeTab,
        matchingChannel.id,
        response.data.metrics
      );
      
      // Find the latest week in actuals
      const latestWeek = Math.max(
        ...currentProduct.actualMetrics.map(metric => metric.week),
        0
      );
      
      // Update the latest week's channel performance
      const updatedActualMetrics = [...currentProduct.actualMetrics];
      const latestWeekIndex = updatedActualMetrics.findIndex(metric => metric.week === latestWeek);
      
      if (latestWeekIndex >= 0) {
        // Update existing week
        const existingChannelPerformance = updatedActualMetrics[latestWeekIndex].channelPerformance || [];
        const existingChannelIndex = existingChannelPerformance.findIndex(
          cp => cp.channelId === matchingChannel.id
        );
        
        if (existingChannelIndex >= 0) {
          // Update existing channel
          existingChannelPerformance[existingChannelIndex] = channelPerformance;
        } else {
          // Add new channel
          existingChannelPerformance.push(channelPerformance);
        }
        
        updatedActualMetrics[latestWeekIndex] = {
          ...updatedActualMetrics[latestWeekIndex],
          channelPerformance: existingChannelPerformance
        };
      } else if (latestWeek > 0) {
        // Create new week based on the previous one
        updatedActualMetrics.push({
          ...updatedActualMetrics[updatedActualMetrics.length - 1],
          week: latestWeek + 1,
          channelPerformance: [channelPerformance]
        });
      } else {
        // Create first week
        updatedActualMetrics.push({
          week: 1,
          revenue: channelPerformance.revenue,
          marketingCost: channelPerformance.spend,
          channelPerformance: [channelPerformance]
        });
      }
      
      // Update product
      updateProduct(currentProduct.info.id, {
        ...currentProduct,
        actualMetrics: updatedActualMetrics
      });
      
      setImportStatus('success');
      setStatusMessage(`Successfully imported data from ${getPlatformDisplayName(activeTab)}`);
    } catch (error) {
      console.error('Import error:', error);
      setImportStatus('error');
      setStatusMessage(`Error importing data: ${error instanceof Error ? error.message : String(error)}`);
    } finally {
      setIsImporting(false);
      setTimeout(() => {
        if (importStatus === 'success' || importStatus === 'error') {
          setImportStatus('idle');
          setStatusMessage('');
        }
      }, 5000);
    }
  };
  
  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-bold tracking-tight">Marketing API Integration</h1>
        <p className="text-sm text-gray-500 mt-1">
          Connect to external marketing platforms to automatically import performance data
        </p>
      </div>
      
      <Tabs value={activeTab} onValueChange={(value) => setActiveTab(value as MarketingPlatform)}>
        <TabsList className="grid grid-cols-6">
          <TabsTrigger value="facebook">Facebook</TabsTrigger>
          <TabsTrigger value="google">Google</TabsTrigger>
          <TabsTrigger value="twitter">Twitter</TabsTrigger>
          <TabsTrigger value="linkedin">LinkedIn</TabsTrigger>
          <TabsTrigger value="tiktok">TikTok</TabsTrigger>
          <TabsTrigger value="custom">Custom</TabsTrigger>
        </TabsList>
        
        {(['facebook', 'google', 'twitter', 'linkedin', 'tiktok', 'custom'] as MarketingPlatform[]).map(platform => (
          <TabsContent key={platform} value={platform}>
            <Card>
              <CardHeader>
                <CardTitle>{getPlatformDisplayName(platform)} Integration</CardTitle>
                <CardDescription>
                  Configure connection to {getPlatformDisplayName(platform)} marketing API
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor={`${platform}-api-key`}>API Key</Label>
                    <Input
                      id={`${platform}-api-key`}
                      type="password"
                      value={formConfig.apiKey}
                      onChange={(e) => handleInputChange('apiKey', e.target.value)}
                      placeholder="Enter your API key"
                    />
                  </div>
                  
                  <div className="space-y-2">
                    <Label htmlFor={`${platform}-endpoint`}>API Endpoint</Label>
                    <Input
                      id={`${platform}-endpoint`}
                      value={formConfig.endpoint}
                      onChange={(e) => handleInputChange('endpoint', e.target.value)}
                      placeholder="API endpoint URL"
                    />
                  </div>
                </div>
                
                <div className="flex items-center space-x-2">
                  <Switch
                    id={`${platform}-active`}
                    checked={formConfig.isActive}
                    onCheckedChange={(checked) => handleInputChange('isActive', checked)}
                  />
                  <Label htmlFor={`${platform}-active`}>Active</Label>
                </div>
                
                {platform === 'custom' && (
                  <div className="space-y-2">
                    <Label htmlFor="custom-platform-name">Platform Name</Label>
                    <Input
                      id="custom-platform-name"
                      value={formConfig.platformName}
                      onChange={(e) => handleInputChange('platformName', e.target.value)}
                      placeholder="Custom platform name"
                    />
                  </div>
                )}
              </CardContent>
              <CardFooter className="flex justify-between">
                <Button variant="outline" onClick={saveApiConfig}>
                  Save Configuration
                </Button>
                
                <div className="flex items-center space-x-2">
                  <Badge variant={apiConfigs[platform] ? "default" : "outline"}>
                    {apiConfigs[platform] ? "Configured" : "Not Configured"}
                  </Badge>
                </div>
              </CardFooter>
            </Card>
            
            {apiConfigs[platform] && (
              <Card className="mt-4">
                <CardHeader>
                  <CardTitle>Import Data</CardTitle>
                  <CardDescription>
                    Import marketing performance data from {getPlatformDisplayName(platform)}
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <Label htmlFor={`${platform}-start-date`}>Start Date</Label>
                      <Input
                        id={`${platform}-start-date`}
                        type="date"
                        value={startDate}
                        onChange={(e) => setStartDate(e.target.value)}
                      />
                    </div>
                    
                    <div className="space-y-2">
                      <Label htmlFor={`${platform}-end-date`}>End Date</Label>
                      <Input
                        id={`${platform}-end-date`}
                        type="date"
                        value={endDate}
                        onChange={(e) => setEndDate(e.target.value)}
                      />
                    </div>
                  </div>
                </CardContent>
                <CardFooter>
                  <Button 
                    onClick={importData} 
                    disabled={isImporting || !apiConfigs[platform]?.isActive}
                  >
                    {isImporting ? (
                      <>
                        <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                        Importing...
                      </>
                    ) : (
                      <>
                        <RefreshCw className="mr-2 h-4 w-4" />
                        Import Data
                      </>
                    )}
                  </Button>
                </CardFooter>
              </Card>
            )}
          </TabsContent>
        ))}
      </Tabs>
      
      {importStatus === 'success' && (
        <Alert variant="success" className="mt-4">
          <AlertTitle>Success</AlertTitle>
          <AlertDescription>{statusMessage}</AlertDescription>
        </Alert>
      )}
      
      {importStatus === 'error' && (
        <Alert variant="destructive" className="mt-4">
          <AlertTitle>Error</AlertTitle>
          <AlertDescription>{statusMessage}</AlertDescription>
        </Alert>
      )}
      
      <div className="border-t pt-4">
        <h2 className="text-lg font-medium mb-2">Connected Platforms</h2>
        <div className="grid grid-cols-3 gap-4">
          {Object.entries(apiConfigs).map(([platform, config]) => {
            if (!config) return null;
            return (
              <Card key={platform}>
                <CardHeader className="pb-2">
                  <CardTitle className="text-base">{config.platformName}</CardTitle>
                </CardHeader>
                <CardContent className="pb-2">
                  <p className="text-sm truncate">{config.endpoint}</p>
                  <Badge 
                    variant={config.isActive ? "success" : "secondary"}
                    className="mt-2"
                  >
                    {config.isActive ? "Active" : "Inactive"}
                  </Badge>
                </CardContent>
                <CardFooter className="pt-0">
                  <Button variant="ghost" size="sm" asChild>
                    <a href={config.endpoint} target="_blank" rel="noopener noreferrer">
                      <ExternalLink className="h-4 w-4 mr-1" />
                      Visit
                    </a>
                  </Button>
                </CardFooter>
              </Card>
            );
          })}
        </div>
      </div>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "./ui/card";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "./ui/table";
import {
  Plus,
  Trash2,
  Save,
  X,
  Pencil,
  BarChart
} from 'lucide-react';
import { 
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger
} from "./ui/dialog";
import { Label } from "./ui/label";
import useStore from '../store/useStore';
import { formatCurrency, formatNumber, formatPercent } from "../lib/utils";
import type { 
  Product, 
  ActualMetrics, 
  MarketingChannelItem,
  MarketingChannelPerformance
} from "../types";

interface MarketingChannelActualsProps {
  weekNumber?: number;
}

const MarketingChannelActuals = ({ weekNumber }: MarketingChannelActualsProps) => {
  const { products, currentProductId, updateProduct } = useStore();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [selectedWeek, setSelectedWeek] = useState<number | undefined>(weekNumber);
  const [weekActuals, setWeekActuals] = useState<ActualMetrics | null>(null);
  const [channelMetrics, setChannelMetrics] = useState<Record<string, ChannelMetricInputs>>({});
  const [isEditing, setIsEditing] = useState(false);
  
  // Get current product
  const currentProduct = products.find(p => p.info.id === currentProductId);
  
  // Get marketing channels
  const marketingChannels = currentProduct?.costMetrics?.marketing?.channels || [];
  
  // Get available actual weeks
  const actualWeeks = (currentProduct?.actualMetrics || [])
    .sort((a, b) => a.week - b.week)
    .map(a => a.week);
  
  // Type for form inputs
  interface ChannelMetricInputs {
    spend: string;
    impressions: string;
    clicks: string;
    conversions: string;
    revenue: string;
  }
  
  // Load actuals for the selected week
  useEffect(() => {
    if (!currentProduct || !selectedWeek) return;
    
    const weekData = (currentProduct.actualMetrics || []).find(
      actual => actual.week === selectedWeek
    );
    
    setWeekActuals(weekData || null);
    
    // Initialize channel metrics
    const metrics: Record<string, ChannelMetricInputs> = {};
    
    // First initialize all channels with empty values
    marketingChannels.forEach(channel => {
      metrics[channel.id] = {
        spend: '',
        impressions: '',
        clicks: '',
        conversions: '',
        revenue: ''
      };
    });
    
    // Then fill in existing data if any
    if (weekData && weekData.channelPerformance) {
      weekData.channelPerformance.forEach(perf => {
        if (metrics[perf.channelId]) {
          const existingMetrics = metrics[perf.channelId];
          metrics[perf.channelId] = {
            spend: perf.spend?.toString() || '',
            impressions: perf.impressions?.toString() || '',
            clicks: perf.clicks?.toString() || '',
            conversions: perf.conversions?.toString() || '',
            revenue: perf.revenue?.toString() || ''
          };
        }
      });
    }
    
    setChannelMetrics(metrics);
  }, [currentProduct, selectedWeek, marketingChannels]);
  
  // Handle saving channel actuals
  const handleSaveChannelData = () => {
    if (!currentProduct || !selectedWeek) return;
    
    // Create channel performance data
    const channelPerformance: MarketingChannelPerformance[] = [];
    
    Object.entries(channelMetrics).forEach(([channelId, metrics]) => {
      // Only include channels with at least one metric
      if (metrics.spend || metrics.impressions || metrics.clicks || metrics.conversions || metrics.revenue) {
        channelPerformance.push({
          channelId,
          spend: metrics.spend ? parseFloat(metrics.spend) : undefined,
          impressions: metrics.impressions ? parseInt(metrics.impressions) : undefined,
          clicks: metrics.clicks ? parseInt(metrics.clicks) : undefined,
          conversions: metrics.conversions ? parseInt(metrics.conversions) : undefined,
          revenue: metrics.revenue ? parseFloat(metrics.revenue) : undefined
        });
      }
    });
    
    // If we already have actuals for this week, update them
    const updatedActualMetrics = [...(currentProduct.actualMetrics || [])];
    const existingIndex = updatedActualMetrics.findIndex(a => a.week === selectedWeek);
    
    if (existingIndex >= 0) {
      // Update existing entry
      updatedActualMetrics[existingIndex] = {
        ...updatedActualMetrics[existingIndex],
        channelPerformance
      };
    } else if (channelPerformance.length > 0) {
      // Create new entry if we have channel data
      updatedActualMetrics.push({
        id: `metrics-${Date.now()}`,
        week: selectedWeek,
        year: new Date().getFullYear(),
        channelPerformance
      });
    }
    
    // Update the product
    updateProduct({
      ...currentProduct,
      actualMetrics: updatedActualMetrics
    });
    
    // Close dialog and reset
    setIsDialogOpen(false);
    setIsEditing(false);
  };
  
  // Calculate derived metrics
  const getChannelMetrics = (channelId: string) => {
    const metrics = channelMetrics[channelId];
    if (!metrics) return null;
    
    const spend = parseFloat(metrics.spend || '0');
    const impressions = parseInt(metrics.impressions || '0');
    const clicks = parseInt(metrics.clicks || '0');
    const conversions = parseInt(metrics.conversions || '0');
    const revenue = parseFloat(metrics.revenue || '0');
    
    const ctr = impressions > 0 ? (clicks / impressions) * 100 : 0;
    const convRate = clicks > 0 ? (conversions / clicks) * 100 : 0;
    const cpc = clicks > 0 ? spend / clicks : 0;
    const cpa = conversions > 0 ? spend / conversions : 0;
    const roi = spend > 0 ? ((revenue - spend) / spend) * 100 : 0;
    
    return { ctr, convRate, cpc, cpa, roi };
  };
  
  // Render the dialog content
  const renderDialogContent = () => (
    <div className="space-y-6">
      {marketingChannels.length === 0 ? (
        <div className="text-center p-4 bg-gray-50 rounded">
          <p className="text-gray-500">No marketing channels defined for this product.</p>
          <p className="text-sm text-gray-500 mt-1">
            Define channels in the Product Setup section to track channel performance.
          </p>
        </div>
      ) : (
        <>
          <div className="flex items-center justify-between mb-4">
            <div>
              <h4 className="text-lg font-medium">Marketing Channel Actuals</h4>
              <p className="text-sm text-gray-500">Week {selectedWeek}</p>
            </div>
            
            <Button 
              variant={isEditing ? "default" : "outline"} 
              size="sm"
              onClick={() => setIsEditing(!isEditing)}
            >
              {isEditing ? "Editing..." : "Edit Data"}
            </Button>
          </div>
          
          <div className="overflow-x-auto">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Channel</TableHead>
                  <TableHead>Spend</TableHead>
                  <TableHead>Impressions</TableHead>
                  <TableHead>Clicks</TableHead>
                  <TableHead>Conversions</TableHead>
                  <TableHead>Revenue</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {marketingChannels.map(channel => {
                  const metrics = channelMetrics[channel.id];
                  
                  return (
                    <TableRow key={channel.id}>
                      <TableCell className="font-medium">{channel.name}</TableCell>
                      
                      {isEditing ? (
                        <>
                          <TableCell>
                            <Input
                              type="number"
                              value={metrics?.spend || ''}
                              onChange={(e) => setChannelMetrics(prev => ({
                                ...prev,
                                [channel.id]: { ...(prev[channel.id] || {}), spend: e.target.value }
                              }))}
                              placeholder="0.00"
                              className="w-24"
                            />
                          </TableCell>
                          <TableCell>
                            <Input
                              type="number"
                              value={metrics?.impressions || ''}
                              onChange={(e) => setChannelMetrics(prev => ({
                                ...prev,
                                [channel.id]: { ...(prev[channel.id] || {}), impressions: e.target.value }
                              }))}
                              placeholder="0"
                              className="w-24"
                            />
                          </TableCell>
                          <TableCell>
                            <Input
                              type="number"
                              value={metrics?.clicks || ''}
                              onChange={(e) => setChannelMetrics(prev => ({
                                ...prev,
                                [channel.id]: { ...(prev[channel.id] || {}), clicks: e.target.value }
                              }))}
                              placeholder="0"
                              className="w-24"
                            />
                          </TableCell>
                          <TableCell>
                            <Input
                              type="number"
                              value={metrics?.conversions || ''}
                              onChange={(e) => setChannelMetrics(prev => ({
                                ...prev,
                                [channel.id]: { ...(prev[channel.id] || {}), conversions: e.target.value }
                              }))}
                              placeholder="0"
                              className="w-24"
                            />
                          </TableCell>
                          <TableCell>
                            <Input
                              type="number"
                              value={metrics?.revenue || ''}
                              onChange={(e) => setChannelMetrics(prev => ({
                                ...prev,
                                [channel.id]: { ...(prev[channel.id] || {}), revenue: e.target.value }
                              }))}
                              placeholder="0.00"
                              className="w-24"
                            />
                          </TableCell>
                        </>
                      ) : (
                        <>
                          <TableCell>{metrics?.spend ? formatCurrency(parseFloat(metrics.spend)) : '-'}</TableCell>
                          <TableCell>{metrics?.impressions ? formatNumber(parseInt(metrics.impressions)) : '-'}</TableCell>
                          <TableCell>{metrics?.clicks ? formatNumber(parseInt(metrics.clicks)) : '-'}</TableCell>
                          <TableCell>{metrics?.conversions ? formatNumber(parseInt(metrics.conversions)) : '-'}</TableCell>
                          <TableCell>{metrics?.revenue ? formatCurrency(parseFloat(metrics.revenue)) : '-'}</TableCell>
                        </>
                      )}
                    </TableRow>
                  );
                })}
              </TableBody>
            </Table>
          </div>
          
          {isEditing && (
            <div className="flex justify-end space-x-2 mt-4">
              <Button variant="outline" onClick={() => setIsEditing(false)}>
                Cancel
              </Button>
              <Button onClick={handleSaveChannelData}>
                Save Channel Data
              </Button>
            </div>
          )}
          
          {/* Derived Metrics Section */}
          {!isEditing && (
            <div className="mt-8">
              <h4 className="text-lg font-medium mb-4">Derived Performance Metrics</h4>
              
              <div className="overflow-x-auto">
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Channel</TableHead>
                      <TableHead>CTR</TableHead>
                      <TableHead>Conv. Rate</TableHead>
                      <TableHead>CPC</TableHead>
                      <TableHead>CPA</TableHead>
                      <TableHead>ROI</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {marketingChannels.map(channel => {
                      const derivedMetrics = getChannelMetrics(channel.id);
                      
                      if (!derivedMetrics) return null;
                      
                      return (
                        <TableRow key={`derived-${channel.id}`}>
                          <TableCell className="font-medium">{channel.name}</TableCell>
                          <TableCell>{formatPercent(derivedMetrics.ctr / 100)}</TableCell>
                          <TableCell>{formatPercent(derivedMetrics.convRate / 100)}</TableCell>
                          <TableCell>{formatCurrency(derivedMetrics.cpc)}</TableCell>
                          <TableCell>{formatCurrency(derivedMetrics.cpa)}</TableCell>
                          <TableCell className={derivedMetrics.roi >= 0 ? 'text-green-600' : 'text-red-600'}>
                            {formatPercent(derivedMetrics.roi / 100)}
                          </TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
  
  return (
    <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
      <DialogTrigger asChild>
        <Button variant="outline" className="mt-4" onClick={() => setSelectedWeek(weekNumber)}>
          <BarChart className="h-4 w-4 mr-2" />
          Track Channel Performance
        </Button>
      </DialogTrigger>
      <DialogContent className="max-w-4xl">
        <DialogHeader>
          <DialogTitle>Marketing Channel Performance</DialogTitle>
          <DialogDescription>
            Record and analyze performance metrics for each marketing channel
          </DialogDescription>
        </DialogHeader>
        
        {renderDialogContent()}
        
        <DialogFooter>
          <Button variant="outline" onClick={() => setIsDialogOpen(false)}>
            Close
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
};

export default MarketingChannelActuals;

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from './ui/card';
import { Button } from './ui/button';
import { Input } from './ui/input';
import { Label } from './ui/label';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from './ui/table';
import { Tabs, TabsContent, TabsList, TabsTrigger } from './ui/tabs';
import { Progress } from './ui/progress';
import { LineChart, Line, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { PlusCircle, Trash2, Save, Edit, Check, X, TrendingUp, BarChart2 } from 'lucide-react';
import useStore from '../store/useStore';
import { formatCurrency, formatNumber, formatPercent } from '../lib/utils';
import * as db from '../lib/database';

// KPI Types
interface KPI {
  id: string;
  name: string;
  category: 'revenue' | 'engagement' | 'conversion' | 'cost';
  target: number;
  current: number;
  unit: 'currency' | 'percent' | 'number';
  timeframe: 'weekly' | 'monthly' | 'quarterly';
  startDate: string;
  endDate: string;
  productId?: string;
}

// Category data type
interface CategoryProgress {
  category: string;
  progress: number;
  count: number;
}

// Chart data type
interface ChartData {
  name: string;
  target: number;
  current: number;
  progress: number;
  category: string;
}

export default function MarketingKPITracker() {
  const { products, currentProductId } = useStore();
  const [kpis, setKpis] = useState<KPI[]>([]);
  const [newKpi, setNewKpi] = useState<Partial<KPI>>({
    category: 'revenue',
    unit: 'currency',
    timeframe: 'weekly',
    target: 0,
    current: 0,
    startDate: new Date().toISOString().split('T')[0],
    endDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]
  });
  const [isAddingKpi, setIsAddingKpi] = useState(false);
  const [editingKpiId, setEditingKpiId] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<'all' | 'revenue' | 'engagement' | 'conversion' | 'cost'>('all');
  const [isLoading, setIsLoading] = useState(true);
  
  // Get current product
  const currentProduct = products.find(p => p.info.id === currentProductId);
  
  // Load KPIs from database
  useEffect(() => {
    const loadKpis = async () => {
      if (!currentProductId) return;
      
      setIsLoading(true);
      try {
        const productKpis = await db.getMarketingKPIs(currentProductId);
        setKpis(productKpis as KPI[]);
      } catch (error) {
        console.error('Error loading marketing KPIs:', error);
        
        // If there's an error, try to load from localStorage as fallback
        const storedKpis = localStorage.getItem(`marketing-kpis-${currentProductId}`);
        
        if (storedKpis) {
          setKpis(JSON.parse(storedKpis));
        } else {
          // Initialize with example KPIs
          const exampleKpis: KPI[] = [
            {
              id: '1',
              name: 'Weekly Revenue',
              category: 'revenue',
              target: 10000,
              current: 7500,
              unit: 'currency',
              timeframe: 'weekly',
              startDate: new Date().toISOString().split('T')[0],
              endDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
              productId: currentProductId
            },
            {
              id: '2',
              name: 'Conversion Rate',
              category: 'conversion',
              target: 3.5,
              current: 2.8,
              unit: 'percent',
              timeframe: 'weekly',
              startDate: new Date().toISOString().split('T')[0],
              endDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
              productId: currentProductId
            },
            {
              id: '3',
              name: 'Cost per Acquisition',
              category: 'cost',
              target: 25,
              current: 32,
              unit: 'currency',
              timeframe: 'weekly',
              startDate: new Date().toISOString().split('T')[0],
              endDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
              productId: currentProductId
            },
            {
              id: '4',
              name: 'Social Media Engagement',
              category: 'engagement',
              target: 5000,
              current: 3200,
              unit: 'number',
              timeframe: 'weekly',
              startDate: new Date().toISOString().split('T')[0],
              endDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
              productId: currentProductId
            }
          ];
          
          // Save example KPIs to Firestore
          await Promise.all(exampleKpis.map(kpi => db.addMarketingKPI(currentProductId, kpi)));
          setKpis(exampleKpis);
        }
      } finally {
        setIsLoading(false);
      }
    };

    loadKpis();
  }, [currentProductId]);
  
  if (!currentProduct) {
    return (
      <div className="text-center py-8 text-gray-500">
        No product selected or product not found.
      </div>
    );
  }
  
  // Filter KPIs based on active tab
  const filteredKpis = activeTab === 'all' 
    ? kpis 
    : kpis.filter(kpi => kpi.category === activeTab);
  
  // Format KPI value based on unit
  const formatKpiValue = (value: number, unit: KPI['unit']) => {
    switch (unit) {
      case 'currency':
        return formatCurrency(value);
      case 'percent':
        return `${value.toFixed(1)}%`;
      case 'number':
        return formatNumber(value);
      default:
        return value.toString();
    }
  };
  
  // Calculate progress percentage
  const calculateProgress = (current: number, target: number, category: KPI['category']) => {
    // For cost metrics, lower is better
    if (category === 'cost') {
      if (current <= target) return 100;
      if (target === 0) return 0;
      return Math.max(0, Math.min(100, (target / current) * 100));
    }
    
    // For other metrics, higher is better
    if (target === 0) return current > 0 ? 100 : 0;
    return Math.max(0, Math.min(100, (current / target) * 100));
  };
  
  // Get progress color
  const getProgressColor = (kpi: KPI) => {
    const percentage = calculateProgress(kpi.current, kpi.target, kpi.category);
    if (percentage >= 100) return "bg-green-500";
    if (percentage >= 80) return "bg-yellow-500";
    if (percentage >= 60) return "bg-orange-500";
    return "bg-red-500";
  };
  
  // Handle input change for new KPI
  const handleNewKpiChange = (field: keyof KPI, value: unknown) => {
    setNewKpi(prev => ({
      ...prev,
      [field]: value
    }));
  };
  
  // Add new KPI
  const addKpi = async () => {
    if (!newKpi.name || !currentProductId) return;
    
    const kpi: KPI = {
      id: Date.now().toString(),
      name: newKpi.name || '',
      category: newKpi.category || 'revenue',
      target: newKpi.target || 0,
      current: newKpi.current || 0,
      unit: newKpi.unit || 'currency',
      timeframe: newKpi.timeframe || 'weekly',
      startDate: newKpi.startDate || new Date().toISOString().split('T')[0],
      endDate: newKpi.endDate || new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
      productId: currentProductId
    };
    
    try {
      // Add KPI to Firestore
      const newId = await db.addMarketingKPI(currentProductId, kpi);
      
      // Update local state with the new KPI
      const updatedKpi = { ...kpi, id: newId };
      const updatedKpis = [...kpis, updatedKpi];
      setKpis(updatedKpis);
      
      // Reset form
      setNewKpi({
        category: 'revenue',
        unit: 'currency',
        timeframe: 'weekly',
        target: 0,
        current: 0,
        startDate: new Date().toISOString().split('T')[0],
        endDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]
      });
      setIsAddingKpi(false);
    } catch (error) {
      console.error('Error adding KPI:', error);
    }
  };
  
  // Update KPI
  const updateKpi = async (id: string, updates: Partial<KPI>) => {
    if (!currentProductId) return;
    
    try {
      // Update KPI in Firestore
      await db.updateMarketingKPI(currentProductId, id, updates);
      
      // Update local state
      const updatedKpis = kpis.map(kpi => 
        kpi.id === id ? { ...kpi, ...updates } : kpi
      );
      
      setKpis(updatedKpis);
      setEditingKpiId(null);
    } catch (error) {
      console.error('Error updating KPI:', error);
    }
  };
  
  // Delete KPI
  const deleteKpi = async (id: string) => {
    if (!currentProductId) return;
    
    try {
      // Delete KPI from Firestore
      await db.deleteMarketingKPI(currentProductId, id);
      
      // Update local state
      const updatedKpis = kpis.filter(kpi => kpi.id !== id);
      setKpis(updatedKpis);
    } catch (error) {
      console.error('Error deleting KPI:', error);
    }
  };
  
  // Prepare chart data
  const chartData = kpis.map(kpi => ({
    name: kpi.name,
    target: kpi.target,
    current: kpi.current,
    progress: calculateProgress(kpi.current, kpi.target, kpi.category),
    category: kpi.category
  }));
  
  // Group KPIs by category for summary
  const kpisByCategory = kpis.reduce((acc: Record<string, KPI[]>, kpi) => {
    if (!acc[kpi.category]) {
      acc[kpi.category] = [];
    }
    acc[kpi.category].push(kpi);
    return acc;
  }, {} as Record<string, KPI[]>);
  
  // Calculate average progress by category
  const categoryProgress = Object.entries(kpisByCategory).map(([category, categoryKpis]) => {
    const avgProgress = categoryKpis.reduce((sum: number, kpi) => 
      sum + calculateProgress(kpi.current, kpi.target, kpi.category), 0
    ) / categoryKpis.length;
    
    return {
      category,
      progress: avgProgress,
      count: categoryKpis.length
    };
  });
  
  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold tracking-tight">Marketing KPI Tracker</h1>
          <p className="text-sm text-gray-500 mt-1">
            Set targets and track progress for key marketing performance indicators
          </p>
        </div>
        <Button onClick={() => setIsAddingKpi(true)} disabled={isAddingKpi || isLoading}>
          <PlusCircle className="h-4 w-4 mr-2" />
          Add KPI
        </Button>
      </div>
      
      {isLoading ? (
        <div className="flex justify-center items-center py-12">
          <div className="flex flex-col items-center">
            <div className="w-10 h-10 border-2 border-t-blue-600 border-r-transparent border-b-blue-600 border-l-transparent rounded-full animate-spin mb-4"></div>
            <p className="text-gray-500">Loading marketing KPIs...</p>
          </div>
        </div>
      ) : (
        <>
          {isAddingKpi && (
            <Card>
              <CardHeader>
                <CardTitle>Add New KPI</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="kpi-name">KPI Name</Label>
                    <Input
                      id="kpi-name"
                      value={newKpi.name || ''}
                      onChange={(e) => handleNewKpiChange('name', e.target.value)}
                      placeholder="e.g., Conversion Rate"
                    />
                  </div>
                  
                  <div className="space-y-2">
                    <Label htmlFor="kpi-category">Category</Label>
                    <select
                      id="kpi-category"
                      className="w-full p-2 border rounded"
                      value={newKpi.category}
                      onChange={(e) => handleNewKpiChange('category', e.target.value)}
                    >
                      <option value="revenue">Revenue</option>
                      <option value="engagement">Engagement</option>
                      <option value="conversion">Conversion</option>
                      <option value="cost">Cost</option>
                    </select>
                  </div>
                  
                  <div className="space-y-2">
                    <Label htmlFor="kpi-target">Target Value</Label>
                    <Input
                      id="kpi-target"
                      type="number"
                      value={newKpi.target || 0}
                      onChange={(e) => handleNewKpiChange('target', Number(e.target.value))}
                    />
                  </div>
                  
                  <div className="space-y-2">
                    <Label htmlFor="kpi-current">Current Value</Label>
                    <Input
                      id="kpi-current"
                      type="number"
                      value={newKpi.current || 0}
                      onChange={(e) => handleNewKpiChange('current', Number(e.target.value))}
                    />
                  </div>
                  
                  <div className="space-y-2">
                    <Label htmlFor="kpi-unit">Unit</Label>
                    <select
                      id="kpi-unit"
                      className="w-full p-2 border rounded"
                      value={newKpi.unit}
                      onChange={(e) => handleNewKpiChange('unit', e.target.value)}
                    >
                      <option value="currency">Currency ($)</option>
                      <option value="percent">Percentage (%)</option>
                      <option value="number">Number</option>
                    </select>
                  </div>
                  
                  <div className="space-y-2">
                    <Label htmlFor="kpi-timeframe">Timeframe</Label>
                    <select
                      id="kpi-timeframe"
                      className="w-full p-2 border rounded"
                      value={newKpi.timeframe}
                      onChange={(e) => handleNewKpiChange('timeframe', e.target.value)}
                    >
                      <option value="weekly">Weekly</option>
                      <option value="monthly">Monthly</option>
                      <option value="quarterly">Quarterly</option>
                    </select>
                  </div>
                  
                  <div className="space-y-2">
                    <Label htmlFor="kpi-start-date">Start Date</Label>
                    <Input
                      id="kpi-start-date"
                      type="date"
                      value={newKpi.startDate}
                      onChange={(e) => handleNewKpiChange('startDate', e.target.value)}
                    />
                  </div>
                  
                  <div className="space-y-2">
                    <Label htmlFor="kpi-end-date">End Date</Label>
                    <Input
                      id="kpi-end-date"
                      type="date"
                      value={newKpi.endDate}
                      onChange={(e) => handleNewKpiChange('endDate', e.target.value)}
                    />
                  </div>
                </div>
              </CardContent>
              <CardFooter className="flex justify-end space-x-2">
                <Button variant="outline" onClick={() => setIsAddingKpi(false)}>
                  Cancel
                </Button>
                <Button onClick={addKpi}>
                  <Check className="h-4 w-4 mr-2" />
                  Add KPI
                </Button>
              </CardFooter>
            </Card>
          )}
          
          {categoryProgress.length > 0 ? (
            <div className="grid md:grid-cols-4 gap-4">
              {categoryProgress.map((category) => (
                <Card key={category.category}>
                  <CardHeader className="pb-2">
                    <CardTitle className="text-base capitalize">{category.category} KPIs</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="text-2xl font-bold mb-1">
                      {category.progress.toFixed(0)}%
                    </div>
                    <Progress value={category.progress} className="h-2 mb-2" />
                    <p className="text-sm text-gray-500">
                      {category.count} KPI{category.count !== 1 ? 's' : ''}
                    </p>
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : (
            <Card>
              <CardContent className="py-8 text-center text-gray-500">
                <p>No KPIs added yet. Click "Add KPI" to get started.</p>
              </CardContent>
            </Card>
          )}
          
          <Tabs value={activeTab} onValueChange={(value) => setActiveTab(value as any)}>
            <TabsList>
              <TabsTrigger value="all">All KPIs</TabsTrigger>
              <TabsTrigger value="revenue">Revenue</TabsTrigger>
              <TabsTrigger value="engagement">Engagement</TabsTrigger>
              <TabsTrigger value="conversion">Conversion</TabsTrigger>
              <TabsTrigger value="cost">Cost</TabsTrigger>
            </TabsList>
            
            <TabsContent value={activeTab} className="mt-4">
              <Card>
                <CardHeader>
                  <CardTitle>{activeTab === 'all' ? 'All KPIs' : `${activeTab.charAt(0).toUpperCase() + activeTab.slice(1)} KPIs`}</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="overflow-x-auto">
                    <Table>
                      <TableHeader>
                        <TableRow>
                          <TableHead>KPI</TableHead>
                          <TableHead>Category</TableHead>
                          <TableHead>Target</TableHead>
                          <TableHead>Current</TableHead>
                          <TableHead>Progress</TableHead>
                          <TableHead>Timeframe</TableHead>
                          <TableHead className="text-right">Actions</TableHead>
                        </TableRow>
                      </TableHeader>
                      <TableBody>
                        {filteredKpis.map((kpi) => (
                          <TableRow key={kpi.id}>
                            <TableCell className="font-medium">{kpi.name}</TableCell>
                            <TableCell className="capitalize">{kpi.category}</TableCell>
                            <TableCell>{formatKpiValue(kpi.target, kpi.unit)}</TableCell>
                            <TableCell>
                              {editingKpiId === kpi.id ? (
                                <Input
                                  type="number"
                                  value={kpi.current}
                                  onChange={(e) => updateKpi(kpi.id, { current: Number(e.target.value) })}
                                  className="w-24"
                                />
                              ) : (
                                formatKpiValue(kpi.current, kpi.unit)
                              )}
                            </TableCell>
                            <TableCell>
                              <div className="flex items-center">
                                <Progress 
                                  value={calculateProgress(kpi.current, kpi.target, kpi.category)}
                                  className="h-2 w-full"
                                  indicatorClassName={getProgressColor(kpi)}
                                />
                                <span className="ml-2 w-10 text-xs">
                                  {calculateProgress(kpi.current, kpi.target, kpi.category).toFixed(0)}%
                                </span>
                              </div>
                            </TableCell>
                            <TableCell className="capitalize">{kpi.timeframe}</TableCell>
                            <TableCell className="text-right">
                              <div className="flex justify-end space-x-2">
                                {editingKpiId === kpi.id ? (
                                  <Button 
                                    size="sm" 
                                    variant="ghost" 
                                    onClick={() => setEditingKpiId(null)}
                                  >
                                    <Check className="h-4 w-4" />
                                  </Button>
                                ) : (
                                  <Button 
                                    size="sm" 
                                    variant="ghost" 
                                    onClick={() => setEditingKpiId(kpi.id)}
                                  >
                                    <Edit className="h-4 w-4" />
                                  </Button>
                                )}
                                <Button 
                                  size="sm" 
                                  variant="ghost" 
                                  onClick={() => deleteKpi(kpi.id)}
                                >
                                  <Trash2 className="h-4 w-4" />
                                </Button>
                              </div>
                            </TableCell>
                          </TableRow>
                        ))}
                        {filteredKpis.length === 0 && (
                          <TableRow>
                            <TableCell colSpan={7} className="text-center py-4 text-gray-500">
                              No KPIs found. Add some to start tracking!
                            </TableCell>
                          </TableRow>
                        )}
                      </TableBody>
                    </Table>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
          
          {kpis.length > 0 && (
            <div className="grid md:grid-cols-2 gap-6">
              <Card>
                <CardHeader>
                  <CardTitle>KPI Progress</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="h-[300px]">
                    <ResponsiveContainer width="100%" height="100%">
                      <BarChart data={chartData}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="name" />
                        <YAxis />
                        <Tooltip formatter={(value: unknown) => [`${value}%`, 'Progress']} />
                        <Legend />
                        <Bar dataKey="progress" name="Progress (%)" fill="#3b82f6" />
                      </BarChart>
                    </ResponsiveContainer>
                  </div>
                </CardContent>
              </Card>
              
              <Card>
                <CardHeader>
                  <CardTitle>Target vs Current</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="h-[300px]">
                    <ResponsiveContainer width="100%" height="100%">
                      <BarChart data={chartData}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="name" />
                        <YAxis />
                        <Tooltip />
                        <Legend />
                        <Bar dataKey="target" name="Target" fill="#22c55e" />
                        <Bar dataKey="current" name="Current" fill="#3b82f6" />
                      </BarChart>
                    </ResponsiveContainer>
                  </div>
                </CardContent>
              </Card>
            </div>
          )}
        </>
      )}
    </div>
  );
}

import React, { useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import { Card, CardContent, CardHeader, CardTitle } from './ui/card';
import { Button } from './ui/button';
import { BarChart2, PieChart, TrendingUp, DollarSign, Users, ShoppingBag } from 'lucide-react';
import { Breadcrumbs } from './ui/breadcrumb';
import useStore from '../store/useStore';
import {
  ResponsiveContainer,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  PieChart as RechartsPieChart,
  Pie,
  Cell,
  LineChart,
  Line
} from 'recharts';

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884d8'];

export default function PortfolioView() {
  const navigate = useNavigate();
  const { products } = useStore();

  // Calculate aggregate metrics across all products
  const aggregateMetrics = useMemo(() => {
    console.log("Products:", products);
    
    if (!products || products.length === 0) {
      return {
        totalRevenue: 0,
        totalCosts: 0,
        totalProfit: 0,
        averageProfit: 0,
        totalProjectedEvents: 0,
        totalProjectedVisitors: 0,
        productCount: 0,
        mostProfitableProduct: null,
        leastProfitableProduct: null,
      };
    }

    let totalRevenue = 0;
    let totalCosts = 0;
    let totalProfit = 0;
    let totalProjectedEvents = 0;
    let totalProjectedVisitors = 0;
    let highestProfit = -Infinity;
    let lowestProfit = Infinity;
    let mostProfitableProduct = null;
    let leastProfitableProduct = null;

    products.forEach(product => {
      console.log(`Processing product: ${product.info.name}`);
      console.log(`Weekly projections:`, product.weeklyProjections);
      
      // Safety check if weeklyProjections exists and has items
      if (!product.weeklyProjections || product.weeklyProjections.length === 0) {
        console.warn(`No weekly projections found for product: ${product.info.name}`);
        return; // Skip this product
      }
      
      // Sum up projected revenue and costs from weekly projections
      const productRevenue = product.weeklyProjections.reduce(
        (sum, proj) => {
          // Check if totalRevenue exists and is a number
          if (proj && typeof proj.totalRevenue === 'number') {
            return sum + proj.totalRevenue;
          }
          return sum;
        }, 
        0
      );
      
      const productCosts = product.weeklyProjections.reduce(
        (sum, proj) => {
          // Check if totalCosts exists and is a number
          if (proj && typeof proj.totalCosts === 'number') {
            return sum + proj.totalCosts;
          }
          return sum;
        }, 
        0
      );
      
      console.log(`Product: ${product.info.name}, Revenue: ${productRevenue}, Costs: ${productCosts}`);
      
      const productProfit = productRevenue - productCosts;
      
      totalRevenue += productRevenue;
      totalCosts += productCosts;
      totalProfit += productProfit;
      
      // Count total projected events and visitors with safety checks
      totalProjectedEvents += product.weeklyProjections.reduce(
        (sum, proj) => {
          if (proj && typeof proj.numberOfEvents === 'number') {
            return sum + proj.numberOfEvents;
          }
          return sum;
        }, 
        0
      );
      
      totalProjectedVisitors += product.weeklyProjections.reduce(
        (sum, proj) => {
          if (proj && typeof proj.footTraffic === 'number') {
            return sum + proj.footTraffic;
          }
          return sum;
        }, 
        0
      );
      
      // Find most and least profitable products
      if (productProfit > highestProfit) {
        highestProfit = productProfit;
        mostProfitableProduct = product;
      }
      
      if (productProfit < lowestProfit) {
        lowestProfit = productProfit;
        leastProfitableProduct = product;
      }
    });

    console.log("Total revenue:", totalRevenue);
    console.log("Total costs:", totalCosts);
    console.log("Total profit:", totalProfit);

    return {
      totalRevenue,
      totalCosts,
      totalProfit,
      averageProfit: products.length > 0 ? totalProfit / products.length : 0,
      totalProjectedEvents,
      totalProjectedVisitors,
      productCount: products.length,
      mostProfitableProduct,
      leastProfitableProduct,
    };
  }, [products]);

  // Prepare data for revenue by product type chart
  const revenueByTypeData = useMemo(() => {
    const typeMap = new Map();
    
    products.forEach(product => {
      const type = product.info.type;
      
      // Skip if there are no weekly projections
      if (!product.weeklyProjections || product.weeklyProjections.length === 0) {
        return;
      }
      
      const revenue = product.weeklyProjections.reduce(
        (sum, proj) => {
          // Check if totalRevenue exists and is a number
          if (proj && typeof proj.totalRevenue === 'number') {
            return sum + proj.totalRevenue;
          }
          return sum;
        }, 
        0
      );
      
      if (typeMap.has(type)) {
        typeMap.set(type, typeMap.get(type) + revenue);
      } else {
        typeMap.set(type, revenue);
      }
    });
    
    return Array.from(typeMap.entries()).map(([name, revenue]) => ({
      name,
      revenue
    }));
  }, [products]);

  // Prepare data for revenue vs costs chart per product
  const productComparisonData = useMemo(() => {
    return products
      .filter(product => product.weeklyProjections && product.weeklyProjections.length > 0)
      .map(product => {
        const revenue = product.weeklyProjections.reduce(
          (sum, proj) => {
            if (proj && typeof proj.totalRevenue === 'number') {
              return sum + proj.totalRevenue;
            }
            return sum;
          }, 
          0
        );
        
        const costs = product.weeklyProjections.reduce(
          (sum, proj) => {
            if (proj && typeof proj.totalCosts === 'number') {
              return sum + proj.totalCosts;
            }
            return sum;
          }, 
          0
        );
        
        return {
          name: product.info.name,
          revenue,
          costs,
          profit: revenue - costs
        };
      });
  }, [products]);

  // Prepare data for cumulative profit over time across all products
  const cumulativeProfitData = useMemo(() => {
    if (!products || products.length === 0) return [];
    
    // Find the max number of weeks in any product
    const maxWeeks = Math.max(
      ...products.map(p => p.weeklyProjections.length)
    );
    
    // Initialize array with zeros for each week
    const weeklyData = Array(maxWeeks).fill(0).map((_, i) => ({
      week: i + 1,
      profit: 0
    }));
    
    // Sum up profits for each week across all products
    products.forEach(product => {
      product.weeklyProjections.forEach(proj => {
        if (proj.week > 0 && proj.week <= maxWeeks) {
          weeklyData[proj.week - 1].profit += proj.weeklyProfit;
        }
      });
    });
    
    // Calculate cumulative totals
    let cumulativeTotal = 0;
    return weeklyData.map(week => {
      cumulativeTotal += week.profit;
      return {
        week: week.week,
        profit: week.profit,
        cumulativeProfit: cumulativeTotal
      };
    });
  }, [products]);

  return (
    <div className="container mx-auto px-4 py-8">
      <Breadcrumbs
        items={[
          { label: 'Home', link: '/' },
          { label: 'Portfolio' }
        ]}
        onNavigate={navigate}
      />
      
      <div className="flex justify-between items-center mb-8">
        <div className="flex items-center gap-3">
          <BarChart2 className="h-8 w-8 text-blue-600" />
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Portfolio Dashboard</h1>
            <p className="text-gray-600 text-sm">
              Holistic view of all your products and their performance
            </p>
          </div>
        </div>
        <Button 
          onClick={() => navigate('/')}
          variant="outline"
        >
          Back to Home
        </Button>
      </div>

      {/* Key Metrics Section */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
        <Card className="shadow-md border-0">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-500">Total Revenue</p>
                <h3 className="text-2xl font-bold text-gray-900">
                  ${aggregateMetrics.totalRevenue.toLocaleString()}
                </h3>
              </div>
              <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center">
                <DollarSign className="h-6 w-6 text-blue-600" />
              </div>
            </div>
          </CardContent>
        </Card>
        
        <Card className="shadow-md border-0">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-500">Total Profit</p>
                <h3 className="text-2xl font-bold text-gray-900">
                  ${aggregateMetrics.totalProfit.toLocaleString()}
                </h3>
              </div>
              <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center">
                <TrendingUp className="h-6 w-6 text-green-600" />
              </div>
            </div>
          </CardContent>
        </Card>
        
        <Card className="shadow-md border-0">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-500">Total Projected Events</p>
                <h3 className="text-2xl font-bold text-gray-900">
                  {aggregateMetrics.totalProjectedEvents.toLocaleString()}
                </h3>
              </div>
              <div className="w-12 h-12 bg-purple-100 rounded-full flex items-center justify-center">
                <ShoppingBag className="h-6 w-6 text-purple-600" />
              </div>
            </div>
          </CardContent>
        </Card>
        
        <Card className="shadow-md border-0">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-500">Projected Visitors</p>
                <h3 className="text-2xl font-bold text-gray-900">
                  {aggregateMetrics.totalProjectedVisitors.toLocaleString()}
                </h3>
              </div>
              <div className="w-12 h-12 bg-amber-100 rounded-full flex items-center justify-center">
                <Users className="h-6 w-6 text-amber-600" />
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Product Analysis Section */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
        <Card className="shadow-md border-0">
          <CardHeader>
            <CardTitle>Product Revenue vs. Costs</CardTitle>
          </CardHeader>
          <CardContent>
            {productComparisonData.length > 0 ? (
              <ResponsiveContainer width="100%" height={300}>
                <BarChart
                  data={productComparisonData}
                  margin={{ top: 20, right: 30, left: 20, bottom: 80 }}
                >
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis 
                    dataKey="name" 
                    angle={-45} 
                    textAnchor="end" 
                    height={80} 
                  />
                  <YAxis />
                  <Tooltip 
                    formatter={(value) => [`$${value.toLocaleString()}`, '']}
                  />
                  <Legend />
                  <Bar dataKey="revenue" fill="#0088FE" name="Revenue" />
                  <Bar dataKey="costs" fill="#FF8042" name="Costs" />
                </BarChart>
              </ResponsiveContainer>
            ) : (
              <div className="flex items-center justify-center h-48">
                <p className="text-gray-500">No product data available</p>
              </div>
            )}
          </CardContent>
        </Card>
        
        <Card className="shadow-md border-0">
          <CardHeader>
            <CardTitle>Revenue by Product Type</CardTitle>
          </CardHeader>
          <CardContent>
            {revenueByTypeData.length > 0 ? (
              <ResponsiveContainer width="100%" height={300}>
                <RechartsPieChart>
                  <Pie
                    data={revenueByTypeData}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    outerRadius={100}
                    fill="#8884d8"
                    dataKey="revenue"
                    nameKey="name"
                    label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
                  >
                    {revenueByTypeData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip formatter={(value) => [`$${value.toLocaleString()}`, 'Revenue']} />
                </RechartsPieChart>
              </ResponsiveContainer>
            ) : (
              <div className="flex items-center justify-center h-48">
                <p className="text-gray-500">No product type data available</p>
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      {/* Cumulative Profit Chart */}
      <Card className="shadow-md border-0 mb-8">
        <CardHeader>
          <CardTitle>Portfolio Cumulative Profit Over Time</CardTitle>
        </CardHeader>
        <CardContent>
          {cumulativeProfitData.length > 0 ? (
            <ResponsiveContainer width="100%" height={300}>
              <LineChart
                data={cumulativeProfitData}
                margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
              >
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="week" />
                <YAxis />
                <Tooltip 
                  formatter={(value) => [`$${value.toLocaleString()}`, '']}
                />
                <Legend />
                <Line 
                  type="monotone" 
                  dataKey="profit" 
                  stroke="#0088FE" 
                  name="Weekly Profit" 
                />
                <Line 
                  type="monotone" 
                  dataKey="cumulativeProfit" 
                  stroke="#00C49F" 
                  name="Cumulative Profit" 
                />
              </LineChart>
            </ResponsiveContainer>
          ) : (
            <div className="flex items-center justify-center h-48">
              <p className="text-gray-500">No profit data available</p>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Products List */}
      <Card className="shadow-md border-0">
        <CardHeader>
          <CardTitle>Your Products</CardTitle>
        </CardHeader>
        <CardContent>
          {products.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {products.map(product => (
                <Card key={product.info.id} className="hover:shadow-lg transition-shadow cursor-pointer" onClick={() => navigate(`/product/${product.info.id}`)}>
                  <CardContent className="p-4">
                    <div className="flex items-center gap-3 mb-3">
                      {product.info.logo ? (
                        <img 
                          src={product.info.logo} 
                          alt={product.info.name} 
                          className="w-10 h-10 rounded object-cover"
                        />
                      ) : (
                        <div className="w-10 h-10 bg-gray-200 rounded flex items-center justify-center">
                          <span className="text-gray-500 font-bold">
                            {product.info.name.substring(0, 2).toUpperCase()}
                          </span>
                        </div>
                      )}
                      <div>
                        <h3 className="font-semibold text-gray-900">{product.info.name}</h3>
                        <p className="text-xs text-gray-500">{product.info.type}</p>
                      </div>
                    </div>
                    
                    <div className="grid grid-cols-2 gap-2 text-sm">
                      <div>
                        <p className="text-gray-500">Revenue</p>
                        <p className="font-semibold">${product.weeklyProjections.reduce(
                          (sum, proj) => sum + proj.totalRevenue, 
                          0
                        ).toLocaleString()}</p>
                      </div>
                      <div>
                        <p className="text-gray-500">Profit</p>
                        <p className="font-semibold">${(product.weeklyProjections.reduce(
                          (sum, proj) => sum + proj.totalRevenue, 
                          0
                        ) - product.weeklyProjections.reduce(
                          (sum, proj) => sum + proj.totalCosts, 
                          0
                        )).toLocaleString()}</p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : (
            <div className="flex flex-col items-center justify-center py-8">
              <p className="text-gray-500 mb-4">No products available</p>
              <Button onClick={() => navigate('/')}>Create Your First Product</Button>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from './ui/card';
import { Button } from './ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from './ui/select';
import { Slider } from './ui/slider';
import { Input } from './ui/input';
import { Label } from './ui/label';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from './ui/table';
import { Tabs, TabsContent, TabsList, TabsTrigger } from './ui/tabs';
import { PlusCircle, Trash2, Save, Copy, PieChart, BarChart, ArrowUpDown, Download } from 'lucide-react';
import { 
  PieChart as RechartsChart, 
  Pie, 
  Cell, 
  ResponsiveContainer, 
  Tooltip, 
  Legend, 
  BarChart as RechartsBarChart, 
  Bar, 
  XAxis, 
  YAxis, 
  CartesianGrid 
} from 'recharts';
import useStore from '../store/useStore';
import { ScenarioModel, Product, WeeklyProjection } from '../types';
import { formatCurrency, formatPercent } from '../lib/utils';
import { exportScenarioComparison } from '../lib/exportUtils';

// Colors for charts
const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884d8', '#82ca9d'];

export default function ProductBasedScenarioModeling() {
  const { 
    products, 
    currentProductId, 
    scenarios,
    addScenarioModel, 
    updateScenarioModel, 
    deleteScenarioModel,
    getScenariosByProduct
  } = useStore();
  
  // Selected product state
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [baselineData, setBaselineData] = useState<WeeklyProjection[]>([]);
  
  // Scenario management state
  const [activeScenario, setActiveScenario] = useState<ScenarioModel | null>(null);
  const [isCreatingScenario, setIsCreatingScenario] = useState(false);
  const [activeTab, setActiveTab] = useState<string>('parameters');
  
  // Load current product when component mounts or currentProductId changes
  useEffect(() => {
    if (currentProductId) {
      const product = products.find(p => p.info.id === currentProductId);
      if (product) {
        setSelectedProduct(product);
        setBaselineData(product.weeklyProjections || []);
        
        // Load scenarios for this product
        const productScenarios = getScenariosByProduct(currentProductId);
        if (productScenarios.length > 0 && !activeScenario) {
          setActiveScenario(productScenarios[0]);
        }
      }
    }
  }, [currentProductId, products, getScenariosByProduct, activeScenario]);
  
  // Create a default blank scenario for the selected product
  const createDefaultScenario = () => {
    if (!selectedProduct) return;
    
    const newScenario: ScenarioModel = {
      id: crypto.randomUUID(),
      name: `${selectedProduct.info.name} - New Scenario`,
      productId: selectedProduct.info.id,
      description: `Scenario for ${selectedProduct.info.name}`,
      modifiers: {
        revenue: {
          ticketRevenue: 0,
          fbRevenue: 0,
          merchandiseRevenue: 0,
          digitalRevenue: 0
        },
        costs: {
          marketingCost: 0,
          staffingCost: 0,
          eventCost: 0,
          setupCost: 0
        },
        attendance: {
          footTraffic: 0
        }
      },
      createdAt: new Date().toISOString(),
      lastModified: new Date().toISOString(),
      variant: 'custom'
    };
    
    addScenarioModel(newScenario);
    setActiveScenario(newScenario);
    setIsCreatingScenario(false);
  };
  
  // Handle product selection change
  const handleProductChange = (productId: string) => {
    const product = products.find(p => p.info.id === productId);
    if (product) {
      setSelectedProduct(product);
      setBaselineData(product.weeklyProjections || []);
      
      // Load scenarios for this product
      const productScenarios = getScenariosByProduct(productId);
      setActiveScenario(productScenarios.length > 0 ? productScenarios[0] : null);
    }
  };
  
  // Calculate scenario projections by applying modifiers to baseline data
  const calculateScenarioProjections = (scenario: ScenarioModel) => {
    if (!baselineData || baselineData.length === 0) return [];
    
    return baselineData.map(week => {
      const { modifiers } = scenario;
      
      // Apply percentage adjustments to baseline values
      return {
        ...week,
        projectedTicketRevenue: week.projectedTicketRevenue * (1 + modifiers.revenue.ticketRevenue / 100),
        projectedFbRevenue: week.projectedFbRevenue * (1 + modifiers.revenue.fbRevenue / 100),
        projectedMerchandiseRevenue: week.projectedMerchandiseRevenue * (1 + modifiers.revenue.merchandiseRevenue / 100),
        projectedDigitalRevenue: week.projectedDigitalRevenue * (1 + modifiers.revenue.digitalRevenue / 100),
        projectedMarketingCost: week.projectedMarketingCost * (1 + modifiers.costs.marketingCost / 100),
        projectedStaffingCost: week.projectedStaffingCost * (1 + modifiers.costs.staffingCost / 100),
        projectedEventCost: week.projectedEventCost * (1 + modifiers.costs.eventCost / 100),
        projectedSetupCost: week.projectedSetupCost * (1 + modifiers.costs.setupCost / 100),
        projectedFootTraffic: week.projectedFootTraffic * (1 + modifiers.attendance.footTraffic / 100)
      };
    });
  };
  
  // Update a scenario modifier
  const updateModifier = (
    category: 'revenue' | 'costs' | 'attendance',
    key: string,
    value: number
  ) => {
    if (!activeScenario) return;
    
    const updatedScenario = {
      ...activeScenario,
      modifiers: {
        ...activeScenario.modifiers,
        [category]: {
          ...activeScenario.modifiers[category],
          [key]: value
        }
      },
      lastModified: new Date().toISOString()
    };
    
    updateScenarioModel(updatedScenario);
    setActiveScenario(updatedScenario);
  };
  
  // Save the current scenario
  const saveScenario = (name: string, description: string, variant: 'optimistic' | 'pessimistic' | 'neutral' | 'custom' = 'custom') => {
    if (!activeScenario || !selectedProduct) return;
    
    const updatedScenario = {
      ...activeScenario,
      name,
      description,
      variant,
      lastModified: new Date().toISOString()
    };
    
    updateScenarioModel(updatedScenario);
    setActiveScenario(updatedScenario);
  };
  
  // Delete a scenario
  const handleDeleteScenario = (scenarioId: string) => {
    if (window.confirm('Are you sure you want to delete this scenario?')) {
      deleteScenarioModel(scenarioId);
      
      // Select a different scenario if the active one was deleted
      if (activeScenario?.id === scenarioId) {
        const remainingScenarios = getScenariosByProduct(selectedProduct?.info.id || '');
        setActiveScenario(remainingScenarios.length > 0 ? remainingScenarios[0] : null);
      }
    }
  };
  
  // Duplicate a scenario
  const handleDuplicateScenario = (scenarioId: string) => {
    const scenarioToDuplicate = scenarios.find(s => s.id === scenarioId);
    if (!scenarioToDuplicate || !selectedProduct) return;
    
    const newScenario: ScenarioModel = {
      ...scenarioToDuplicate,
      id: crypto.randomUUID(),
      name: `${scenarioToDuplicate.name} (Copy)`,
      createdAt: new Date().toISOString(),
      lastModified: new Date().toISOString()
    };
    
    addScenarioModel(newScenario);
    setActiveScenario(newScenario);
  };
  
  // Export scenario comparison
  const handleExportScenario = () => {
    if (!activeScenario || !selectedProduct) return;
    
    const scenarioData = calculateScenarioProjections(activeScenario);
    exportScenarioComparison(
      selectedProduct,
      baselineData,
      scenarioData,
      activeScenario.name
    );
  };
  
  // Calculate totals for baseline and scenario data for charts and tables
  const calculateTotals = () => {
    if (!baselineData || baselineData.length === 0) {
      return { baseline: null, scenario: null };
    }
    
    const baselineTotals = {
      ticketRevenue: baselineData.reduce((sum, week) => sum + week.projectedTicketRevenue, 0),
      fbRevenue: baselineData.reduce((sum, week) => sum + week.projectedFbRevenue, 0),
      merchandiseRevenue: baselineData.reduce((sum, week) => sum + week.projectedMerchandiseRevenue, 0),
      digitalRevenue: baselineData.reduce((sum, week) => sum + week.projectedDigitalRevenue, 0),
      totalRevenue: baselineData.reduce((sum, week) => 
        sum + week.projectedTicketRevenue + week.projectedFbRevenue + 
        week.projectedMerchandiseRevenue + week.projectedDigitalRevenue, 0),
      
      marketingCost: baselineData.reduce((sum, week) => sum + week.projectedMarketingCost, 0),
      staffingCost: baselineData.reduce((sum, week) => sum + week.projectedStaffingCost, 0),
      eventCost: baselineData.reduce((sum, week) => sum + week.projectedEventCost, 0),
      setupCost: baselineData.reduce((sum, week) => sum + week.projectedSetupCost, 0),
      totalCost: baselineData.reduce((sum, week) => 
        sum + week.projectedMarketingCost + week.projectedStaffingCost + 
        week.projectedEventCost + week.projectedSetupCost, 0),
      
      footTraffic: baselineData.reduce((sum, week) => sum + week.projectedFootTraffic, 0)
    };
    
    baselineTotals.profit = baselineTotals.totalRevenue - baselineTotals.totalCost;
    
    if (!activeScenario) return { baseline: baselineTotals, scenario: null };
    
    const scenarioData = calculateScenarioProjections(activeScenario);
    
    const scenarioTotals = {
      ticketRevenue: scenarioData.reduce((sum, week) => sum + week.projectedTicketRevenue, 0),
      fbRevenue: scenarioData.reduce((sum, week) => sum + week.projectedFbRevenue, 0),
      merchandiseRevenue: scenarioData.reduce((sum, week) => sum + week.projectedMerchandiseRevenue, 0),
      digitalRevenue: scenarioData.reduce((sum, week) => sum + week.projectedDigitalRevenue, 0),
      totalRevenue: scenarioData.reduce((sum, week) => 
        sum + week.projectedTicketRevenue + week.projectedFbRevenue + 
        week.projectedMerchandiseRevenue + week.projectedDigitalRevenue, 0),
      
      marketingCost: scenarioData.reduce((sum, week) => sum + week.projectedMarketingCost, 0),
      staffingCost: scenarioData.reduce((sum, week) => sum + week.projectedStaffingCost, 0),
      eventCost: scenarioData.reduce((sum, week) => sum + week.projectedEventCost, 0),
      setupCost: scenarioData.reduce((sum, week) => sum + week.projectedSetupCost, 0),
      totalCost: scenarioData.reduce((sum, week) => 
        sum + week.projectedMarketingCost + week.projectedStaffingCost + 
        week.projectedEventCost + week.projectedSetupCost, 0),
      
      footTraffic: scenarioData.reduce((sum, week) => sum + week.projectedFootTraffic, 0)
    };
    
    scenarioTotals.profit = scenarioTotals.totalRevenue - scenarioTotals.totalCost;
    
    return { baseline: baselineTotals, scenario: scenarioTotals };
  };
  
  // Format percentage change between baseline and scenario
  const formatChange = (baseline: number, scenario: number) => {
    if (baseline === 0) return 'N/A';
    const change = ((scenario - baseline) / baseline) * 100;
    return `${change >= 0 ? '+' : ''}${change.toFixed(2)}%`;
  };
  
  // Render product selector
  const renderProductSelector = () => (
    <Select
      value={selectedProduct?.info.id || ''}
      onValueChange={handleProductChange}
    >
      <SelectTrigger className="w-full">
        <SelectValue placeholder="Select a product" />
      </SelectTrigger>
      <SelectContent>
        {products.map(product => (
          <SelectItem key={product.info.id} value={product.info.id}>
            {product.info.name}
          </SelectItem>
        ))}
      </SelectContent>
    </Select>
  );
  
  // Render scenario selection panel
  const renderScenarioPanel = () => {
    const productScenarios = selectedProduct 
      ? getScenariosByProduct(selectedProduct.info.id)
      : [];
    
    return (
      <Card className="mb-4">
        <CardHeader>
          <CardTitle className="text-xl">Scenarios</CardTitle>
          <CardDescription>
            Create and manage scenarios for {selectedProduct?.info.name || 'selected product'}
          </CardDescription>
        </CardHeader>
        <CardContent>
          {productScenarios.length > 0 ? (
            <div className="space-y-2">
              {productScenarios.map(scenario => (
                <div 
                  key={scenario.id}
                  className={`p-2 border rounded-md flex justify-between items-center cursor-pointer ${
                    activeScenario?.id === scenario.id ? 'bg-blue-50 border-blue-300' : ''
                  }`}
                  onClick={() => setActiveScenario(scenario)}
                >
                  <div>
                    <div className="font-medium">{scenario.name}</div>
                    <div className="text-sm text-gray-500">{scenario.variant}</div>
                  </div>
                  <div className="flex gap-1">
                    <Button 
                      variant="ghost" 
                      size="sm"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDuplicateScenario(scenario.id);
                      }}
                    >
                      <Copy className="h-4 w-4" />
                    </Button>
                    <Button 
                      variant="ghost" 
                      size="sm"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteScenario(scenario.id);
                      }}
                    >
                      <Trash2 className="h-4 w-4 text-red-500" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-4 text-gray-500">
              No scenarios created yet.
            </div>
          )}
          
          <div className="mt-4">
            <Button 
              className="w-full"
              onClick={createDefaultScenario}
            >
              <PlusCircle className="h-4 w-4 mr-2" /> Create New Scenario
            </Button>
          </div>
        </CardContent>
      </Card>
    );
  };
  
  // Render parameter modification controls
  const renderModifierControls = () => {
    if (!activeScenario) return null;
    
    return (
      <Tabs 
        value={activeTab} 
        onValueChange={setActiveTab}
        className="w-full"
      >
        <TabsList className="grid grid-cols-3 mb-4">
          <TabsTrigger value="parameters">Revenue</TabsTrigger>
          <TabsTrigger value="costs">Costs</TabsTrigger>
          <TabsTrigger value="attendance">Attendance</TabsTrigger>
        </TabsList>
        
        <TabsContent value="parameters" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Revenue Adjustments</CardTitle>
              <CardDescription>Adjust revenue projections relative to baseline</CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="space-y-2">
                <div className="flex justify-between">
                  <Label>Ticket Revenue: {activeScenario.modifiers.revenue.ticketRevenue}%</Label>
                  <span>
                    {formatCurrency(calculateTotals().baseline?.ticketRevenue || 0)} โ 
                    {formatCurrency(calculateTotals().scenario?.ticketRevenue || 0)}
                  </span>
                </div>
                <Slider 
                  value={[activeScenario.modifiers.revenue.ticketRevenue]} 
                  min={-50} 
                  max={50} 
                  step={1}
                  onValueChange={(value) => updateModifier('revenue', 'ticketRevenue', value[0])}
                />
              </div>
              
              <div className="space-y-2">
                <div className="flex justify-between">
                  <Label>F&B Revenue: {activeScenario.modifiers.revenue.fbRevenue}%</Label>
                  <span>
                    {formatCurrency(calculateTotals().baseline?.fbRevenue || 0)} โ 
                    {formatCurrency(calculateTotals().scenario?.fbRevenue || 0)}
                  </span>
                </div>
                <Slider 
                  value={[activeScenario.modifiers.revenue.fbRevenue]} 
                  min={-50} 
                  max={50} 
                  step={1}
                  onValueChange={(value) => updateModifier('revenue', 'fbRevenue', value[0])}
                />
              </div>
              
              <div className="space-y-2">
                <div className="flex justify-between">
                  <Label>Merchandise Revenue: {activeScenario.modifiers.revenue.merchandiseRevenue}%</Label>
                  <span>
                    {formatCurrency(calculateTotals().baseline?.merchandiseRevenue || 0)} โ 
                    {formatCurrency(calculateTotals().scenario?.merchandiseRevenue || 0)}
                  </span>
                </div>
                <Slider 
                  value={[activeScenario.modifiers.revenue.merchandiseRevenue]} 
                  min={-50} 
                  max={50} 
                  step={1}
                  onValueChange={(value) => updateModifier('revenue', 'merchandiseRevenue', value[0])}
                />
              </div>
              
              <div className="space-y-2">
                <div className="flex justify-between">
                  <Label>Digital Revenue: {activeScenario.modifiers.revenue.digitalRevenue}%</Label>
                  <span>
                    {formatCurrency(calculateTotals().baseline?.digitalRevenue || 0)} โ 
                    {formatCurrency(calculateTotals().scenario?.digitalRevenue || 0)}
                  </span>
                </div>
                <Slider 
                  value={[activeScenario.modifiers.revenue.digitalRevenue]} 
                  min={-50} 
                  max={50} 
                  step={1}
                  onValueChange={(value) => updateModifier('revenue', 'digitalRevenue', value[0])}
                />
              </div>
              
              <div className="pt-4 border-t border-gray-200">
                <div className="font-medium flex justify-between">
                  <span>Total Revenue Impact</span>
                  <span className={calculateTotals().scenario && calculateTotals().baseline && 
                    calculateTotals().scenario.totalRevenue > calculateTotals().baseline.totalRevenue 
                    ? 'text-green-600' 
                    : 'text-red-600'
                  }>
                    {calculateTotals().baseline && calculateTotals().scenario && 
                      formatChange(calculateTotals().baseline.totalRevenue, calculateTotals().scenario.totalRevenue)
                    }
                  </span>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
        
        <TabsContent value="costs" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Cost Adjustments</CardTitle>
              <CardDescription>Adjust cost projections relative to baseline</CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="space-y-2">
                <div className="flex justify-between">
                  <Label>Marketing Cost: {activeScenario.modifiers.costs.marketingCost}%</Label>
                  <span>
                    {formatCurrency(calculateTotals().baseline?.marketingCost || 0)} โ 
                    {formatCurrency(calculateTotals().scenario?.marketingCost || 0)}
                  </span>
                </div>
                <Slider 
                  value={[activeScenario.modifiers.costs.marketingCost]} 
                  min={-50} 
                  max={50} 
                  step={1}
                  onValueChange={(value) => updateModifier('costs', 'marketingCost', value[0])}
                />
              </div>
              
              <div className="space-y-2">
                <div className="flex justify-between">
                  <Label>Staffing Cost: {activeScenario.modifiers.costs.staffingCost}%</Label>
                  <span>
                    {formatCurrency(calculateTotals().baseline?.staffingCost || 0)} โ 
                    {formatCurrency(calculateTotals().scenario?.staffingCost || 0)}
                  </span>
                </div>
                <Slider 
                  value={[activeScenario.modifiers.costs.staffingCost]} 
                  min={-50} 
                  max={50} 
                  step={1}
                  onValueChange={(value) => updateModifier('costs', 'staffingCost', value[0])}
                />
              </div>
              
              <div className="space-y-2">
                <div className="flex justify-between">
                  <Label>Event Cost: {activeScenario.modifiers.costs.eventCost}%</Label>
                  <span>
                    {formatCurrency(calculateTotals().baseline?.eventCost || 0)} โ 
                    {formatCurrency(calculateTotals().scenario?.eventCost || 0)}
                  </span>
                </div>
                <Slider 
                  value={[activeScenario.modifiers.costs.eventCost]} 
                  min={-50} 
                  max={50} 
                  step={1}
                  onValueChange={(value) => updateModifier('costs', 'eventCost', value[0])}
                />
              </div>
              
              <div className="space-y-2">
                <div className="flex justify-between">
                  <Label>Setup Cost: {activeScenario.modifiers.costs.setupCost}%</Label>
                  <span>
                    {formatCurrency(calculateTotals().baseline?.setupCost || 0)} โ 
                    {formatCurrency(calculateTotals().scenario?.setupCost || 0)}
                  </span>
                </div>
                <Slider 
                  value={[activeScenario.modifiers.costs.setupCost]} 
                  min={-50} 
                  max={50} 
                  step={1}
                  onValueChange={(value) => updateModifier('costs', 'setupCost', value[0])}
                />
              </div>
              
              <div className="pt-4 border-t border-gray-200">
                <div className="font-medium flex justify-between">
                  <span>Total Cost Impact</span>
                  <span className={calculateTotals().scenario && calculateTotals().baseline && 
                    calculateTotals().scenario.totalCost < calculateTotals().baseline.totalCost 
                    ? 'text-green-600' 
                    : 'text-red-600'
                  }>
                    {calculateTotals().baseline && calculateTotals().scenario && 
                      formatChange(calculateTotals().baseline.totalCost, calculateTotals().scenario.totalCost)
                    }
                  </span>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
        
        <TabsContent value="attendance" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Attendance Adjustments</CardTitle>
              <CardDescription>Adjust attendance projections relative to baseline</CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="space-y-2">
                <div className="flex justify-between">
                  <Label>Foot Traffic: {activeScenario.modifiers.attendance.footTraffic}%</Label>
                  <span>
                    {(calculateTotals().baseline?.footTraffic || 0).toLocaleString()} โ 
                    {(calculateTotals().scenario?.footTraffic || 0).toLocaleString()}
                  </span>
                </div>
                <Slider 
                  value={[activeScenario.modifiers.attendance.footTraffic]} 
                  min={-50} 
                  max={50} 
                  step={1}
                  onValueChange={(value) => updateModifier('attendance', 'footTraffic', value[0])}
                />
              </div>
              
              <div className="pt-4 border-t border-gray-200">
                <div className="font-medium flex justify-between">
                  <span>Attendance Impact</span>
                  <span className={calculateTotals().scenario && calculateTotals().baseline && 
                    calculateTotals().scenario.footTraffic > calculateTotals().baseline.footTraffic 
                    ? 'text-green-600' 
                    : 'text-red-600'
                  }>
                    {calculateTotals().baseline && calculateTotals().scenario && 
                      formatChange(calculateTotals().baseline.footTraffic, calculateTotals().scenario.footTraffic)
                    }
                  </span>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    );
  };
  
  // Render comparison charts and tables
  const renderComparisonCharts = () => {
    const totals = calculateTotals();
    if (!totals.baseline || !totals.scenario) return null;
    
    // Generate data for charts
    const revenueComparisonData = [
      { name: 'Ticket', baseline: totals.baseline.ticketRevenue, scenario: totals.scenario.ticketRevenue },
      { name: 'F&B', baseline: totals.baseline.fbRevenue, scenario: totals.scenario.fbRevenue },
      { name: 'Merch', baseline: totals.baseline.merchandiseRevenue, scenario: totals.scenario.merchandiseRevenue },
      { name: 'Digital', baseline: totals.baseline.digitalRevenue, scenario: totals.scenario.digitalRevenue }
    ];
    
    const costComparisonData = [
      { name: 'Marketing', baseline: totals.baseline.marketingCost, scenario: totals.scenario.marketingCost },
      { name: 'Staffing', baseline: totals.baseline.staffingCost, scenario: totals.scenario.staffingCost },
      { name: 'Event', baseline: totals.baseline.eventCost, scenario: totals.scenario.eventCost },
      { name: 'Setup', baseline: totals.baseline.setupCost, scenario: totals.scenario.setupCost }
    ];
    
    const summaryComparisonData = [
      { name: 'Revenue', baseline: totals.baseline.totalRevenue, scenario: totals.scenario.totalRevenue },
      { name: 'Costs', baseline: totals.baseline.totalCost, scenario: totals.scenario.totalCost },
      { name: 'Profit', baseline: totals.baseline.profit, scenario: totals.scenario.profit },
      { name: 'Attendance', baseline: totals.baseline.footTraffic, scenario: totals.scenario.footTraffic, isCount: true }
    ];
    
    return (
      <div className="space-y-6">
        <div className="text-xl font-semibold">
          Scenario Impact Analysis
          <Button 
            variant="outline" 
            size="sm"
            className="ml-2"
            onClick={handleExportScenario}
          >
            <Download className="h-4 w-4 mr-1" /> Export Comparison
          </Button>
        </div>
        
        <div className="grid grid-cols-2 gap-4">
          <Card>
            <CardHeader>
              <CardTitle>Revenue Comparison</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="h-64">
                <ResponsiveContainer width="100%" height="100%">
                  <RechartsBarChart data={revenueComparisonData}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="name" />
                    <YAxis tickFormatter={(value) => `$${Math.round(value / 1000)}k`} />
                    <Tooltip formatter={(value) => formatCurrency(Number(value))} />
                    <Legend />
                    <Bar dataKey="baseline" name="Baseline" fill="#8884d8" />
                    <Bar dataKey="scenario" name="Scenario" fill="#82ca9d" />
                  </RechartsBarChart>
                </ResponsiveContainer>
              </div>
            </CardContent>
          </Card>
          
          <Card>
            <CardHeader>
              <CardTitle>Cost Comparison</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="h-64">
                <ResponsiveContainer width="100%" height="100%">
                  <RechartsBarChart data={costComparisonData}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="name" />
                    <YAxis tickFormatter={(value) => `$${Math.round(value / 1000)}k`} />
                    <Tooltip formatter={(value) => formatCurrency(Number(value))} />
                    <Legend />
                    <Bar dataKey="baseline" name="Baseline" fill="#8884d8" />
                    <Bar dataKey="scenario" name="Scenario" fill="#82ca9d" />
                  </RechartsBarChart>
                </ResponsiveContainer>
              </div>
            </CardContent>
          </Card>
        </div>
        
        <Card>
          <CardHeader>
            <CardTitle>Summary Comparison</CardTitle>
          </CardHeader>
          <CardContent>
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Metric</TableHead>
                  <TableHead className="text-right">Baseline</TableHead>
                  <TableHead className="text-right">Scenario</TableHead>
                  <TableHead className="text-right">Difference</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {summaryComparisonData.map((item) => {
                  const isImprovement = item.name === 'Costs' 
                    ? item.scenario < item.baseline
                    : item.scenario > item.baseline;
                  
                  return (
                    <TableRow key={item.name}>
                      <TableCell>{item.name}</TableCell>
                      <TableCell className="text-right">
                        {item.isCount 
                          ? Math.round(item.baseline).toLocaleString() 
                          : formatCurrency(item.baseline)}
                      </TableCell>
                      <TableCell className="text-right">
                        {item.isCount 
                          ? Math.round(item.scenario).toLocaleString() 
                          : formatCurrency(item.scenario)}
                      </TableCell>
                      <TableCell className={`text-right ${isImprovement ? 'text-green-600' : 'text-red-600'}`}>
                        {formatChange(item.baseline, item.scenario)}
                      </TableCell>
                    </TableRow>
                  );
                })}
              </TableBody>
            </Table>
          </CardContent>
        </Card>
      </div>
    );
  };
  
  // Main component render
  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-3xl font-bold">Product-Based Scenario Modeling</h2>
        <div className="w-1/3">
          {renderProductSelector()}
        </div>
      </div>
      
      {selectedProduct ? (
        <div className="grid grid-cols-4 gap-6">
          {/* Scenario selection panel */}
          <div className="col-span-1">
            {renderScenarioPanel()}
          </div>
          
          {/* Scenario content area */}
          {activeScenario ? (
            <div className="col-span-3 space-y-6">
              <Card>
                <CardHeader>
                  <div className="flex justify-between items-center">
                    <CardTitle className="text-2xl">{activeScenario.name}</CardTitle>
                    <div className="text-sm text-gray-500">
                      Last modified: {new Date(activeScenario.lastModified).toLocaleString()}
                    </div>
                  </div>
                  <CardDescription>{activeScenario.description}</CardDescription>
                </CardHeader>
              </Card>
              
              {renderModifierControls()}
              {renderComparisonCharts()}
            </div>
          ) : (
            <div className="col-span-3 flex items-center justify-center h-64 border rounded-lg bg-gray-50">
              <div className="text-center">
                <p className="text-lg text-gray-500 mb-4">No scenario selected</p>
                <Button onClick={createDefaultScenario}>
                  <PlusCircle className="h-4 w-4 mr-2" /> Create New Scenario
                </Button>
              </div>
            </div>
          )}
        </div>
      ) : (
        <div className="p-8 text-center">
          <p className="text-lg text-gray-500">Select a product to start scenario modeling</p>
        </div>
      )}
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Tabs, TabsContent, TabsList, TabsTrigger } from './ui/tabs';
import { Button } from './ui/button';
import { 
  ArrowLeft, 
  Download, 
  BarChart, 
  PieChart, 
  AlertTriangle, 
  TrendingUp, 
  DollarSign, 
  BarChart2, 
  ChevronDown,
  Award,
  FileText,
  ChevronRight,
  FileDown,
  FileUp
} from 'lucide-react';
import { Spinner } from './ui/spinner';
import { Breadcrumbs } from './ui/breadcrumb';
import { RecentlyViewed } from './ui/recently-viewed';
import { QuickJump } from './ui/quick-jump';
import { SetupWizard } from './ui/setup-wizard';
import ProductHeader from './ProductHeader';
import ExecutiveDashboard from './ExecutiveDashboard';
import ForecastRevenue from './ForecastRevenue';
import ForecastCosts from './ForecastCosts';
import WeeklyForecast from './WeeklyForecast';
import FinancialProjections from './FinancialProjections';
import LongTermProjections from './LongTermProjections';
import RiskAssessment from './RiskAssessment';
import ActualsTracker from './ActualsTracker';
import SeasonalAnalysis from './SeasonalAnalysis';
import ProductScenario from './ProductScenario';
import ScenarioModeling from './ScenarioModeling';
import RiskMatrix from './RiskMatrix';
import MarketingAnalytics from './MarketingAnalytics';
import MarketingApiIntegration from './MarketingApiIntegration';
import BudgetAllocation from './BudgetAllocation';
import MarketingKPITracker from './MarketingKPITracker';
import useStore from '../store/useStore';
import { 
  exportToPDF, 
  downloadFile, 
  ReportType, 
  exportToJSON, 
  exportFinancialData,
  exportWeeklyProjections,
  exportActualMetrics,
  exportRevenueBreakdown,
  exportCostAnalysis,
  exportVarianceAnalysis,
  exportMarketingChannelData
} from '../lib/exportUtils';
import { Product } from '../types/custom.d';

export default function ProductDashboard({ currentProduct, updateProduct, isLoading }: { 
  currentProduct: Product; 
  updateProduct: (product: Product) => void;
  isLoading: boolean;
}) {
  const { id } = useParams();
  const navigate = useNavigate();
  const { products, setCurrentProduct } = useStore();
  const [isExporting, setIsExporting] = useState(false);
  const [exportType, setExportType] = useState<ReportType>('financial');
  const [marketingTab, setMarketingTab] = useState<'analytics' | 'api' | 'budget' | 'kpi'>('analytics');
  const [activeTab, setActiveTab] = useState('dashboard');
  const product = products.find(p => p.info.id === id);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  useEffect(() => {
    if (id) {
      setCurrentProduct(id);
    }
  }, [id, setCurrentProduct]);

  // All the export functions remain the same
  // handleExportPDF, handleExportExcel, handleExportJSON functions...

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      const target = event.target;
      if (dropdownOpen && !target.closest('.dropdown-container')) {
        setDropdownOpen(false);
      }
    };
    
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [dropdownOpen]);

  const handleNavigateToProduct = (productId) => {
    navigate(`/product/${productId}`);
  };
  
  const handleQuickJump = (path) => {
    navigate(path);
  };
  
  // Handle tab selection from the setup wizard
  const handleSelectTab = (tabId: string) => {
    console.log('ProductDashboard: Setting active tab to:', tabId);
    // Force active tab to update by ensuring it's a string type
    const safeTabId = String(tabId);
    setActiveTab(safeTabId);
    
    // Add a small timeout to ensure the UI updates
    setTimeout(() => {
      console.log('ProductDashboard: Active tab is now:', activeTab);
      // Additional check - force scroll to the tab content
      const tabContent = document.querySelector(`[data-state="active"][role="tabpanel"]`);
      if (tabContent) {
        tabContent.scrollIntoView({ behavior: 'smooth', block: 'start' });
      }
    }, 100);
  };

  if (!id) {
    return (
      <div className="min-h-screen bg-gray-50 p-4 md:p-8">
        <div className="max-w-6xl mx-auto">
          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-6 text-center">
            <h1 className="text-2xl font-bold text-yellow-800 mb-4">No Product Selected</h1>
            <p className="text-yellow-700 mb-6">Please select a product to view.</p>
            <Button onClick={() => navigate('/')}>Return to Home</Button>
          </div>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="min-h-screen bg-gray-50 p-4 md:p-8">
        <div className="max-w-6xl mx-auto">
          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-6 text-center">
            <h1 className="text-2xl font-bold text-yellow-800 mb-4">Product Not Found</h1>
            <p className="text-yellow-700 mb-6">The requested product could not be found.</p>
            <Button onClick={() => navigate('/')}>Return to Home</Button>
          </div>
        </div>
      </div>
    );
  }

  // Render marketing content based on selected tab
  const renderMarketingContent = () => {
    switch (marketingTab) {
      case 'analytics':
        return <MarketingAnalytics />;
      case 'api':
        return <MarketingApiIntegration />;
      case 'budget':
        return <BudgetAllocation />;
      case 'kpi':
        return <MarketingKPITracker />;
      default:
        return <MarketingAnalytics />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-4 md:p-8">
      <div className="max-w-7xl mx-auto">
        <Breadcrumbs 
          items={[
            { label: 'Products', href: '/' },
            { label: product?.info.name || 'Product Details' }
          ]}
          onNavigate={navigate}
        />
        
        <header className="mb-8">
          <div className="flex items-center justify-between mb-4">
            <Button variant="ghost" onClick={() => navigate('/')} className="mr-4">
              <ArrowLeft className="h-4 w-4 mr-2" />
              Back
            </Button>
            
            {/* Export dropdown button */}
            <div className="relative dropdown-container">
              <Button 
                disabled={isExporting}
                className="flex items-center"
                onClick={() => setDropdownOpen(!dropdownOpen)}
              >
                {isExporting ? (
                  <Spinner className="h-4 w-4 mr-2" />
                ) : (
                  <Download className="h-4 w-4 mr-2" />
                )}
                {isExporting ? 'Exporting...' : 'Export Data'}
                <ChevronDown className="h-4 w-4 ml-2" />
              </Button>
              
              {dropdownOpen && (
                <div className="absolute right-0 z-50 mt-2 w-64 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5">
                  <div className="py-1" role="menu" aria-orientation="vertical">
                    {/* Dropdown content (PDF, Excel, JSON options) */}
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Product header */}
          <ProductHeader info={product.info} />
        </header>

        {/* Main layout with sidebar */}
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
          {/* Main content area - 3/4 width on large screens */}
          <div className="lg:col-span-3">
            {/* Main tab navigation */}
            <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-4">
              <div className="overflow-x-auto pb-2">
                <TabsList className="inline-flex min-w-max w-full md:w-auto">
                  <TabsTrigger value="dashboard" className="whitespace-nowrap">
                    Executive Dashboard
                  </TabsTrigger>
                  <TabsTrigger value="revenue" className="whitespace-nowrap">
                    Revenue Forecast
                  </TabsTrigger>
                  <TabsTrigger value="costs" className="whitespace-nowrap">
                    Cost Forecast
                  </TabsTrigger>
                  <TabsTrigger value="forecast" className="whitespace-nowrap">
                    12-Week Forecast
                  </TabsTrigger>
                  <TabsTrigger value="actuals" className="whitespace-nowrap">
                    Actuals Tracker
                  </TabsTrigger>
                  <TabsTrigger value="marketing" className="whitespace-nowrap">
                    Marketing Analytics
                  </TabsTrigger>
                  <TabsTrigger value="financials" className="whitespace-nowrap">
                    Financial Projections
                  </TabsTrigger>
                  <TabsTrigger value="longterm" className="whitespace-nowrap">
                    Long-term Projections
                  </TabsTrigger>
                  <TabsTrigger value="seasonal" className="whitespace-nowrap">
                    Seasonal Analysis
                  </TabsTrigger>
                  <TabsTrigger value="scenarios" className="whitespace-nowrap">
                    Scenario Modeling
                  </TabsTrigger>
                  <TabsTrigger value="risk" className="whitespace-nowrap">
                    Risk Assessment
                  </TabsTrigger>
                </TabsList>
              </div>

              {/* Tab content */}
              <TabsContent value="dashboard">
                <ExecutiveDashboard />
              </TabsContent>

              <TabsContent value="revenue">
                <ForecastRevenue />
              </TabsContent>

              <TabsContent value="costs">
                <ForecastCosts />
              </TabsContent>

              <TabsContent value="forecast">
                <WeeklyForecast />
              </TabsContent>

              <TabsContent value="actuals">
                <ActualsTracker />
              </TabsContent>

              <TabsContent value="marketing">
                <div className="space-y-4">
                  <Tabs value={marketingTab} onValueChange={(value) => setMarketingTab(value)} className="w-full">
                    <TabsList className="grid grid-cols-4 w-full max-w-md mb-4">
                      <TabsTrigger value="analytics">
                        <BarChart2 className="h-4 w-4 mr-2" />
                        Analytics
                      </TabsTrigger>
                      <TabsTrigger value="api">
                        <TrendingUp className="h-4 w-4 mr-2" />
                        API Import
                      </TabsTrigger>
                      <TabsTrigger value="budget">
                        <DollarSign className="h-4 w-4 mr-2" />
                        Budget
                      </TabsTrigger>
                      <TabsTrigger value="kpi">
                        <Award className="h-4 w-4 mr-2" />
                        KPIs
                      </TabsTrigger>
                    </TabsList>
                  </Tabs>
                  
                  {renderMarketingContent()}
                </div>
              </TabsContent>

              <TabsContent value="financials">
                <FinancialProjections />
              </TabsContent>

              <TabsContent value="longterm">
                <LongTermProjections />
              </TabsContent>
              
              <TabsContent value="seasonal">
                <SeasonalAnalysis />
              </TabsContent>
              
              <TabsContent value="scenarios">
                <ProductScenario />
              </TabsContent>

              <TabsContent value="risk">
                <RiskAssessment />
              </TabsContent>
            </Tabs>
          </div>
          
          {/* Sidebar - 1/4 width on large screens */}
          <div className="space-y-4">
            {/* Setup wizard with ability to select tabs */}
            <SetupWizard 
              onNavigate={handleQuickJump} 
              onSelectTab={handleSelectTab}
            />
            
            {/* Quick jump navigation with tab support */}
            <QuickJump 
              onNavigate={handleQuickJump} 
              onSelectTab={handleSelectTab}
            />
            
            {/* Recently viewed products */}
            <RecentlyViewed onNavigate={handleNavigateToProduct} />
          </div>
        </div>
      </div>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from './ui/card';
import { Button } from './ui/button';
import { Slider } from './ui/slider';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from './ui/select';
import { Input } from './ui/input';
import { Label } from './ui/label';
import { Tabs, TabsContent, TabsList, TabsTrigger } from './ui/tabs';
import useStore from '../store/useStore';
import { Product, ScenarioModel, WeeklyProjection } from '../types';
import { 
  LineChart, Line, XAxis, YAxis, CartesianGrid, 
  Tooltip, Legend, ResponsiveContainer, Bar, BarChart
} from 'recharts';
import { exportScenarioComparison } from '../lib/exportUtils';
import { downloadFile } from '../lib/exportUtils';
import { v4 as uuidv4 } from 'uuid';

const DEFAULT_MODIFIER_VALUE = 0; // 0% change

const ProductScenario = () => {
  const { id: productIdFromRoute } = useParams<{ id: string }>();
  const { products, scenarios, addScenarioModel, updateScenarioModel, deleteScenarioModel, getScenariosByProduct, currentProductId } = useStore();
  
  // Use the product ID from route params if available, otherwise fall back to currentProductId from store
  const [selectedProductId, setSelectedProductId] = useState<string | null>(
    productIdFromRoute || currentProductId || null
  );
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [activeScenarioId, setActiveScenarioId] = useState<string | null>(null);
  const [isEditingName, setIsEditingName] = useState<boolean>(false);
  const [newScenarioName, setNewScenarioName] = useState<string>('');
  const [activeTab, setActiveTab] = useState('editor');
  
  // Update selectedProductId when productIdFromRoute or currentProductId changes
  useEffect(() => {
    if (productIdFromRoute && productIdFromRoute !== selectedProductId) {
      setSelectedProductId(productIdFromRoute);
    } else if (currentProductId && !selectedProductId) {
      setSelectedProductId(currentProductId);
    }
  }, [productIdFromRoute, currentProductId, selectedProductId]);
  
  // Get the selected product's scenarios
  const productScenarios = selectedProductId 
    ? getScenariosByProduct(selectedProductId) 
    : [];
  
  // Get the active scenario
  const activeScenario = activeScenarioId 
    ? productScenarios.find(s => s.id === activeScenarioId) 
    : null;
  
  // Load product when selected
  useEffect(() => {
    if (selectedProductId) {
      const product = products.find(p => p.info.id === selectedProductId);
      setSelectedProduct(product || null);
      
      // Set first scenario as active if available
      const productScenarios = getScenariosByProduct(selectedProductId);
      if (productScenarios.length > 0 && !activeScenarioId) {
        setActiveScenarioId(productScenarios[0].id);
      } else if (productScenarios.length === 0) {
        // If no scenarios exist, create a default one
        createDefaultScenario(selectedProductId);
      }
    }
  }, [selectedProductId, products, getScenariosByProduct, activeScenarioId]);
  
  // Create a default scenario for a product
  const createDefaultScenario = (productId: string) => {
    const newScenario: ScenarioModel = {
      id: uuidv4(),
      name: 'Default Scenario',
      productId,
      description: 'A default scenario based on product baseline',
      modifiers: {
        revenue: {
          ticketRevenue: DEFAULT_MODIFIER_VALUE,
          fbRevenue: DEFAULT_MODIFIER_VALUE,
          merchandiseRevenue: DEFAULT_MODIFIER_VALUE,
          digitalRevenue: DEFAULT_MODIFIER_VALUE,
        },
        costs: {
          marketingCost: DEFAULT_MODIFIER_VALUE,
          staffingCost: DEFAULT_MODIFIER_VALUE,
          eventCost: DEFAULT_MODIFIER_VALUE,
          setupCost: DEFAULT_MODIFIER_VALUE,
        },
        attendance: DEFAULT_MODIFIER_VALUE
      },
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    };
    
    addScenarioModel(newScenario);
    setActiveScenarioId(newScenario.id);
  };
  
  // Create a new scenario
  const createNewScenario = () => {
    if (selectedProductId) {
      const newScenarioName = `Scenario ${productScenarios.length + 1}`;
      
      const newScenario: ScenarioModel = {
        id: uuidv4(),
        name: newScenarioName,
        productId: selectedProductId,
        description: 'New scenario based on product baseline',
        modifiers: {
          revenue: {
            ticketRevenue: DEFAULT_MODIFIER_VALUE,
            fbRevenue: DEFAULT_MODIFIER_VALUE,
            merchandiseRevenue: DEFAULT_MODIFIER_VALUE,
            digitalRevenue: DEFAULT_MODIFIER_VALUE,
          },
          costs: {
            marketingCost: DEFAULT_MODIFIER_VALUE,
            staffingCost: DEFAULT_MODIFIER_VALUE,
            eventCost: DEFAULT_MODIFIER_VALUE,
            setupCost: DEFAULT_MODIFIER_VALUE,
          },
          attendance: DEFAULT_MODIFIER_VALUE
        },
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString(),
      };
      
      addScenarioModel(newScenario);
      setActiveScenarioId(newScenario.id);
    }
  };
  
  // Duplicate active scenario
  const duplicateScenario = () => {
    if (activeScenario && selectedProductId) {
      const duplicatedScenario: ScenarioModel = {
        ...activeScenario,
        id: uuidv4(),
        name: `${activeScenario.name} (Copy)`,
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString(),
      };
      
      addScenarioModel(duplicatedScenario);
      setActiveScenarioId(duplicatedScenario.id);
    }
  };
  
  // Delete active scenario
  const deleteScenario = () => {
    if (activeScenarioId) {
      deleteScenarioModel(activeScenarioId);
      
      // Set first available scenario as active
      if (productScenarios.length > 1) {
        const remainingScenarios = productScenarios.filter(s => s.id !== activeScenarioId);
        setActiveScenarioId(remainingScenarios[0].id);
      } else {
        // If no scenarios left, create a default one
        createDefaultScenario(selectedProductId as string);
      }
    }
  };
  
  // Start scenario name editing
  const startEditingName = () => {
    if (activeScenario) {
      setNewScenarioName(activeScenario.name);
      setIsEditingName(true);
    }
  };
  
  // Save scenario name
  const saveScenarioName = () => {
    if (activeScenario && newScenarioName.trim() !== '') {
      updateScenarioModel({
        ...activeScenario,
        name: newScenarioName.trim(),
        updatedAt: new Date().toISOString(),
      });
      setIsEditingName(false);
    }
  };
  
  // Update scenario modifier
  const updateModifier = (
    category: 'revenue' | 'costs' | 'attendance',
    type: string | null,
    value: number
  ) => {
    if (activeScenario) {
      const updatedScenario = { ...activeScenario };
      
      if (category === 'revenue' && type) {
        updatedScenario.modifiers.revenue[type as keyof typeof updatedScenario.modifiers.revenue] = value;
      } else if (category === 'costs' && type) {
        updatedScenario.modifiers.costs[type as keyof typeof updatedScenario.modifiers.costs] = value;
      } else if (category === 'attendance') {
        updatedScenario.modifiers.attendance = value;
      }
      
      updatedScenario.updatedAt = new Date().toISOString();
      updateScenarioModel(updatedScenario);
    }
  };
  
  // Apply modifiers to generate projections
  const generateProjectedData = (): { baseline: WeeklyProjection[], scenario: WeeklyProjection[] } => {
    if (!selectedProduct || !activeScenario) {
      return { baseline: [], scenario: [] };
    }
    
    // Get baseline projections
    const baseline = selectedProduct.weeklyProjections || [];
    
    // Generate scenario projections with modifiers applied
    const scenario = baseline.map(week => {
      const modifiers = activeScenario.modifiers;
      
      // Apply revenue modifiers
      const ticketRevenue = week.ticketRevenue * (1 + modifiers.revenue.ticketRevenue / 100);
      const fbRevenue = week.fbRevenue * (1 + modifiers.revenue.fbRevenue / 100);
      const merchandiseRevenue = week.merchandiseRevenue * (1 + modifiers.revenue.merchandiseRevenue / 100);
      const digitalRevenue = week.digitalRevenue * (1 + modifiers.revenue.digitalRevenue / 100);
      
      // Apply cost modifiers
      const marketingCosts = week.marketingCosts * (1 + modifiers.costs.marketingCost / 100);
      const staffingCosts = week.staffingCosts * (1 + modifiers.costs.staffingCost / 100);
      const eventCosts = week.eventCosts * (1 + modifiers.costs.eventCost / 100);
      const setupCosts = week.setupCosts * (1 + modifiers.costs.setupCost / 100);
      
      // Apply attendance modifier
      const footTraffic = week.footTraffic * (1 + modifiers.attendance / 100);
      
      return {
        ...week,
        ticketRevenue: ticketRevenue,
        fbRevenue: fbRevenue,
        merchandiseRevenue: merchandiseRevenue,
        digitalRevenue: digitalRevenue,
        marketingCosts: marketingCosts,
        staffingCosts: staffingCosts,
        eventCosts: eventCosts,
        setupCosts: setupCosts,
        footTraffic: footTraffic,
        // Recalculate totals
        totalRevenue: ticketRevenue + fbRevenue + merchandiseRevenue + digitalRevenue,
        totalCosts: marketingCosts + staffingCosts + eventCosts + setupCosts,
        weeklyProfit: (ticketRevenue + fbRevenue + merchandiseRevenue + digitalRevenue) - 
                     (marketingCosts + staffingCosts + eventCosts + setupCosts)
      };
    });
    
    return { baseline, scenario };
  };
  
  // Generate chart data
  const generateChartData = () => {
    const { baseline, scenario } = generateProjectedData();
    if (baseline.length === 0 || scenario.length === 0) return { chartData: [], summaryData: {} };
    
    // Map the data to format suitable for charts
    const chartData = baseline.map((week, index) => {
      const scenarioWeek = scenario[index];
      
      // Calculate total revenues
      const baselineRevenue = week.totalRevenue;
      const scenarioRevenue = scenarioWeek.totalRevenue;
      
      // Calculate total costs
      const baselineCost = week.totalCosts;
      const scenarioCost = scenarioWeek.totalCosts;
      
      // Calculate profits
      const baselineProfit = baselineRevenue - baselineCost;
      const scenarioProfit = scenarioRevenue - scenarioCost;
      
      return {
        week: week.week,
        baselineRevenue,
        scenarioRevenue,
        revenueDifference: scenarioRevenue - baselineRevenue,
        revenuePercentChange: ((scenarioRevenue - baselineRevenue) / baselineRevenue) * 100,
        baselineCost,
        scenarioCost,
        costDifference: scenarioCost - baselineCost,
        costPercentChange: ((scenarioCost - baselineCost) / baselineCost) * 100,
        baselineProfit,
        scenarioProfit,
        profitDifference: scenarioProfit - baselineProfit,
        profitPercentChange: baselineProfit === 0 ? 0 : ((scenarioProfit - baselineProfit) / Math.abs(baselineProfit)) * 100,
        baselineAttendance: week.footTraffic,
        scenarioAttendance: scenarioWeek.footTraffic
      };
    });
    
    // Calculate summary data
    let baselineTotalRevenue = 0;
    let scenarioTotalRevenue = 0;
    let baselineTotalCost = 0;
    let scenarioTotalCost = 0;
    let baselineTotalProfit = 0;
    let scenarioTotalProfit = 0;
    let baselineTotalAttendance = 0;
    let scenarioTotalAttendance = 0;
    
    baseline.forEach((week, index) => {
      const scenarioWeek = scenario[index];
      
      // Sum revenues
      baselineTotalRevenue += week.totalRevenue;
      scenarioTotalRevenue += scenarioWeek.totalRevenue;
      
      // Sum costs
      baselineTotalCost += week.totalCosts;
      scenarioTotalCost += scenarioWeek.totalCosts;
      
      // Sum attendance
      baselineTotalAttendance += week.footTraffic;
      scenarioTotalAttendance += scenarioWeek.footTraffic;
    });
    
    baselineTotalProfit = baselineTotalRevenue - baselineTotalCost;
    scenarioTotalProfit = scenarioTotalRevenue - scenarioTotalCost;
    
    const summaryData = {
      totalBaselineRevenue: baselineTotalRevenue,
      totalScenarioRevenue: scenarioTotalRevenue,
      revenueDifference: scenarioTotalRevenue - baselineTotalRevenue,
      revenuePercentChange: ((scenarioTotalRevenue - baselineTotalRevenue) / baselineTotalRevenue) * 100,
      
      totalBaselineCost: baselineTotalCost,
      totalScenarioCost: scenarioTotalCost,
      costDifference: scenarioTotalCost - baselineTotalCost,
      costPercentChange: ((scenarioTotalCost - baselineTotalCost) / baselineTotalCost) * 100,
      
      totalBaselineProfit: baselineTotalProfit,
      totalScenarioProfit: scenarioTotalProfit,
      profitDifference: scenarioTotalProfit - baselineTotalProfit,
      profitPercentChange: baselineTotalProfit === 0 ? 0 : ((scenarioTotalProfit - baselineTotalProfit) / Math.abs(baselineTotalProfit)) * 100,
      
      totalBaselineAttendance: baselineTotalAttendance,
      totalScenarioAttendance: scenarioTotalAttendance,
      attendanceDifference: scenarioTotalAttendance - baselineTotalAttendance,
      attendancePercentChange: ((scenarioTotalAttendance - baselineTotalAttendance) / baselineTotalAttendance) * 100
    };
    
    return { chartData, summaryData };
  };
  
  // Generate a list of products for the dropdown
  const productOptions = products.map(product => ({
    value: product.info.id,
    label: product.info.name
  }));
  
  // Handle product selection
  const handleProductChange = (value: string) => {
    setSelectedProductId(value);
  };
  
  // Handle export to Excel
  const handleExportExcel = () => {
    if (selectedProduct && activeScenario) {
      const { baseline, scenario } = generateProjectedData();
      exportScenarioComparison(
        selectedProduct, 
        baseline, 
        scenario, 
        activeScenario.name
      );
    }
  };
  
  // Render the product selector
  const renderProductSelector = () => (
    <div className="mb-6">
      <Label htmlFor="product-select" className="text-sm font-medium">
        Select Product
      </Label>
      <Select 
        onValueChange={(value: string) => handleProductChange(value)}
        value={selectedProductId || ''}
      >
        <SelectTrigger id="product-select" className="w-full">
          <SelectValue placeholder="Select a product" />
        </SelectTrigger>
        <SelectContent>
          {productOptions.map(option => (
            <SelectItem key={option.value} value={option.value}>
              {option.label}
            </SelectItem>
          ))}
        </SelectContent>
      </Select>
    </div>
  );
  
  // Render the scenario list
  const renderScenarioList = () => (
    <div className="space-y-2 mb-4">
      {productScenarios.map(scenario => (
        <Button 
          key={scenario.id}
          variant={scenario.id === activeScenarioId ? "default" : "outline"} 
          className="w-full justify-between"
          onClick={() => setActiveScenarioId(scenario.id)}
        >
          <span>{scenario.name}</span>
          {scenario.id === activeScenarioId && (
            <span className="rounded-full bg-primary-foreground h-2 w-2"></span>
          )}
        </Button>
      ))}
    </div>
  );
  
  // Render the revenue sliders
  const renderRevenueSliders = () => (
    <Card>
      <CardHeader>
        <CardTitle>Revenue Adjustments</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-6">
          <div className="space-y-2">
            <div className="flex justify-between">
              <Label htmlFor="ticket-revenue">Ticket Revenue</Label>
              <span>{activeScenario?.modifiers.revenue.ticketRevenue}%</span>
            </div>
            <Slider 
              id="ticket-revenue"
              min={-100}
              max={100}
              step={1}
              value={[activeScenario?.modifiers.revenue.ticketRevenue || 0]}
              onValueChange={(value: number[]) => updateModifier('revenue', 'ticketRevenue', value[0])}
            />
          </div>
          
          <div className="space-y-2">
            <div className="flex justify-between">
              <Label htmlFor="fb-revenue">F&B Revenue</Label>
              <span>{activeScenario?.modifiers.revenue.fbRevenue}%</span>
            </div>
            <Slider 
              id="fb-revenue"
              min={-100}
              max={100}
              step={1}
              value={[activeScenario?.modifiers.revenue.fbRevenue || 0]}
              onValueChange={(value: number[]) => updateModifier('revenue', 'fbRevenue', value[0])}
            />
          </div>
          
          <div className="space-y-2">
            <div className="flex justify-between">
              <Label htmlFor="merch-revenue">Merchandise Revenue</Label>
              <span>{activeScenario?.modifiers.revenue.merchandiseRevenue}%</span>
            </div>
            <Slider 
              id="merch-revenue"
              min={-100}
              max={100}
              step={1}
              value={[activeScenario?.modifiers.revenue.merchandiseRevenue || 0]}
              onValueChange={(value: number[]) => updateModifier('revenue', 'merchandiseRevenue', value[0])}
            />
          </div>
          
          <div className="space-y-2">
            <div className="flex justify-between">
              <Label htmlFor="digital-revenue">Digital Revenue</Label>
              <span>{activeScenario?.modifiers.revenue.digitalRevenue}%</span>
            </div>
            <Slider 
              id="digital-revenue"
              min={-100}
              max={100}
              step={1}
              value={[activeScenario?.modifiers.revenue.digitalRevenue || 0]}
              onValueChange={(value: number[]) => updateModifier('revenue', 'digitalRevenue', value[0])}
            />
          </div>
        </div>
      </CardContent>
    </Card>
  );
  
  // Render the cost sliders
  const renderCostSliders = () => (
    <Card>
      <CardHeader>
        <CardTitle>Cost Adjustments</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-6">
          <div className="space-y-2">
            <div className="flex justify-between">
              <Label htmlFor="marketing-cost">Marketing</Label>
              <span>{activeScenario?.modifiers.costs.marketingCost}%</span>
            </div>
            <Slider 
              id="marketing-cost"
              min={-100}
              max={100}
              step={1}
              value={[activeScenario?.modifiers.costs.marketingCost || 0]}
              onValueChange={(value: number[]) => updateModifier('costs', 'marketingCost', value[0])}
            />
          </div>
          
          <div className="space-y-2">
            <div className="flex justify-between">
              <Label htmlFor="staffing-cost">Staffing</Label>
              <span>{activeScenario?.modifiers.costs.staffingCost}%</span>
            </div>
            <Slider 
              id="staffing-cost"
              min={-100}
              max={100}
              step={1}
              value={[activeScenario?.modifiers.costs.staffingCost || 0]}
              onValueChange={(value: number[]) => updateModifier('costs', 'staffingCost', value[0])}
            />
          </div>
          
          <div className="space-y-2">
            <div className="flex justify-between">
              <Label htmlFor="event-cost">Event</Label>
              <span>{activeScenario?.modifiers.costs.eventCost}%</span>
            </div>
            <Slider 
              id="event-cost"
              min={-100}
              max={100}
              step={1}
              value={[activeScenario?.modifiers.costs.eventCost || 0]}
              onValueChange={(value: number[]) => updateModifier('costs', 'eventCost', value[0])}
            />
          </div>
          
          <div className="space-y-2">
            <div className="flex justify-between">
              <Label htmlFor="setup-cost">Setup</Label>
              <span>{activeScenario?.modifiers.costs.setupCost}%</span>
            </div>
            <Slider 
              id="setup-cost"
              min={-100}
              max={100}
              step={1}
              value={[activeScenario?.modifiers.costs.setupCost || 0]}
              onValueChange={(value: number[]) => updateModifier('costs', 'setupCost', value[0])}
            />
          </div>
        </div>
      </CardContent>
    </Card>
  );
  
  // Render the attendance slider
  const renderAttendanceSlider = () => (
    <Card>
      <CardHeader>
        <CardTitle>Attendance Adjustments</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-2">
          <div className="flex justify-between">
            <Label htmlFor="foot-traffic">Foot Traffic</Label>
            <span>{activeScenario?.modifiers.attendance}%</span>
          </div>
          <Slider 
            id="foot-traffic"
            min={-100}
            max={100}
            step={1}
            value={[activeScenario?.modifiers.attendance || 0]}
            onValueChange={(value: number[]) => updateModifier('attendance', null, value[0])}
          />
        </div>
      </CardContent>
    </Card>
  );
  
  // Format currency
  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(value);
  };
  
  // Format percentage
  const formatPercentage = (value: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'percent',
      minimumFractionDigits: 1,
      maximumFractionDigits: 1
    }).format(value / 100);
  };
  
  // Get summary for display
  const summary = generateChartData().summaryData;
  
  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle>Product-Based Scenario Modeling</CardTitle>
          <CardDescription>
            Create scenarios by modifying baseline product forecasts
          </CardDescription>
        </CardHeader>
        <CardContent>
          {/* Product Selector */}
          {renderProductSelector()}
          
          {/* Only show content if a product is selected */}
          {selectedProductId && (
            <>
              {/* Scenario Navigator */}
              <div className="mb-6 flex items-center space-x-2">
                <div className="flex-1">
                  <Label htmlFor="scenario-select">Scenario</Label>
                  {renderScenarioList()}
                </div>
                <div className="flex space-x-2 pt-6">
                  <Button onClick={createNewScenario} variant="outline" size="sm">New</Button>
                  <Button onClick={duplicateScenario} variant="outline" size="sm">Duplicate</Button>
                  <Button 
                    onClick={deleteScenario} 
                    variant="outline" 
                    size="sm"
                    disabled={productScenarios.length <= 1}
                  >
                    Delete
                  </Button>
                </div>
              </div>
              
              {/* Scenario Name Editor */}
              {activeScenario && (
                <div className="mb-6">
                  {isEditingName ? (
                    <div className="flex items-center space-x-2">
                      <Input
                        value={newScenarioName}
                        onChange={(e) => setNewScenarioName(e.target.value)}
                        className="flex-1"
                      />
                      <Button onClick={saveScenarioName} size="sm">Save</Button>
                      <Button onClick={() => setIsEditingName(false)} variant="outline" size="sm">Cancel</Button>
                    </div>
                  ) : (
                    <div className="flex items-center justify-between">
                      <h3 className="text-lg font-medium">{activeScenario.name}</h3>
                      <Button onClick={startEditingName} variant="ghost" size="sm">Edit Name</Button>
                    </div>
                  )}
                </div>
              )}
              
              <Tabs value={activeTab} onValueChange={setActiveTab}>
                <TabsList className="mb-4">
                  <TabsTrigger value="editor">Parameters</TabsTrigger>
                  <TabsTrigger value="results">Results</TabsTrigger>
                </TabsList>
                
                {activeScenario && (
                  <>
                    <TabsContent value="editor">
                      {/* Parameter Controls */}
                      <div className="space-y-6">
                        {renderRevenueSliders()}
                        {renderCostSliders()}
                        {renderAttendanceSlider()}
                      </div>
                    </TabsContent>
                    
                    <TabsContent value="results">
                      {/* Summary Section */}
                      <div className="mb-8 grid grid-cols-1 md:grid-cols-3 gap-4">
                        <Card>
                          <CardHeader className="pb-2">
                            <CardTitle className="text-base">Revenue</CardTitle>
                          </CardHeader>
                          <CardContent>
                            <div className="space-y-2">
                              <div className="flex justify-between">
                                <span>Baseline:</span>
                                <span className="font-medium">{formatCurrency(summary.totalBaselineRevenue)}</span>
                              </div>
                              <div className="flex justify-between">
                                <span>Scenario:</span>
                                <span className="font-medium">{formatCurrency(summary.totalScenarioRevenue)}</span>
                              </div>
                              <div className="flex justify-between">
                                <span>Difference:</span>
                                <span className={`font-medium ${summary.revenueDifference >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                                  {formatCurrency(summary.revenueDifference)} ({formatPercentage(summary.revenuePercentChange)})
                                </span>
                              </div>
                            </div>
                          </CardContent>
                        </Card>
                        
                        <Card>
                          <CardHeader className="pb-2">
                            <CardTitle className="text-base">Profit</CardTitle>
                          </CardHeader>
                          <CardContent>
                            <div className="space-y-2">
                              <div className="flex justify-between">
                                <span>Baseline:</span>
                                <span className="font-medium">{formatCurrency(summary.totalBaselineProfit)}</span>
                              </div>
                              <div className="flex justify-between">
                                <span>Scenario:</span>
                                <span className="font-medium">{formatCurrency(summary.totalScenarioProfit)}</span>
                              </div>
                              <div className="flex justify-between">
                                <span>Difference:</span>
                                <span className={`font-medium ${summary.profitDifference >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                                  {formatCurrency(summary.profitDifference)} ({formatPercentage(summary.profitPercentChange)})
                                </span>
                              </div>
                            </div>
                          </CardContent>
                        </Card>
                        
                        <Card>
                          <CardHeader className="pb-2">
                            <CardTitle className="text-base">Attendance</CardTitle>
                          </CardHeader>
                          <CardContent>
                            <div className="space-y-2">
                              <div className="flex justify-between">
                                <span>Baseline:</span>
                                <span className="font-medium">
                                  {summary.totalBaselineAttendance.toLocaleString()}
                                </span>
                              </div>
                              <div className="flex justify-between">
                                <span>Scenario:</span>
                                <span className="font-medium">
                                  {summary.totalScenarioAttendance.toLocaleString()}
                                </span>
                              </div>
                              <div className="flex justify-between">
                                <span>Difference:</span>
                                <span className={`font-medium ${summary.attendanceDifference >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                                  {summary.attendanceDifference.toLocaleString()} ({formatPercentage(summary.attendancePercentChange)})
                                </span>
                              </div>
                            </div>
                          </CardContent>
                        </Card>
                      </div>
                      
                      {/* Revenue Chart */}
                      <div className="mb-8">
                        <h3 className="font-medium mb-4">Revenue Comparison</h3>
                        <div className="h-64">
                          <ResponsiveContainer width="100%" height="100%">
                            <BarChart data={generateChartData().chartData}>
                              <CartesianGrid strokeDasharray="3 3" />
                              <XAxis dataKey="week" />
                              <YAxis tickFormatter={(value) => `$${value / 1000}K`} />
                              <Tooltip 
                                formatter={(value) => [`$${Number(value).toLocaleString()}`, '']}
                                labelFormatter={(label) => label}
                              />
                              <Legend />
                              <Bar dataKey="baselineRevenue" name="Baseline Revenue" fill="#8884d8" />
                              <Bar dataKey="scenarioRevenue" name="Scenario Revenue" fill="#4ade80" />
                            </BarChart>
                          </ResponsiveContainer>
                        </div>
                      </div>
                      
                      {/* Profit Chart */}
                      <div className="mb-8">
                        <h3 className="font-medium mb-4">Profit Comparison</h3>
                        <div className="h-64">
                          <ResponsiveContainer width="100%" height="100%">
                            <LineChart data={generateChartData().chartData}>
                              <CartesianGrid strokeDasharray="3 3" />
                              <XAxis dataKey="week" />
                              <YAxis tickFormatter={(value) => `$${value / 1000}K`} />
                              <Tooltip 
                                formatter={(value) => [`$${Number(value).toLocaleString()}`, '']}
                                labelFormatter={(label) => label}
                              />
                              <Legend />
                              <Line 
                                type="monotone" 
                                dataKey="baselineProfit" 
                                name="Baseline Profit" 
                                stroke="#8884d8" 
                                activeDot={{ r: 8 }} 
                              />
                              <Line 
                                type="monotone" 
                                dataKey="scenarioProfit" 
                                name="Scenario Profit" 
                                stroke="#4ade80" 
                                activeDot={{ r: 8 }} 
                              />
                            </LineChart>
                          </ResponsiveContainer>
                        </div>
                      </div>
                      
                      {/* Attendance Chart */}
                      <div>
                        <h3 className="font-medium mb-4">Attendance Comparison</h3>
                        <div className="h-64">
                          <ResponsiveContainer width="100%" height="100%">
                            <BarChart data={generateChartData().chartData}>
                              <CartesianGrid strokeDasharray="3 3" />
                              <XAxis dataKey="week" />
                              <YAxis />
                              <Tooltip 
                                formatter={(value) => [Number(value).toLocaleString(), '']}
                                labelFormatter={(label) => label}
                              />
                              <Legend />
                              <Bar dataKey="baselineAttendance" name="Baseline Attendance" fill="#8884d8" />
                              <Bar dataKey="scenarioAttendance" name="Scenario Attendance" fill="#4ade80" />
                            </BarChart>
                          </ResponsiveContainer>
                        </div>
                      </div>
                    </TabsContent>
                  </>
                )}
              </Tabs>
            </>
          )}
        </CardContent>
        <CardFooter>
          <div className="flex justify-between w-full">
            <Button variant="outline" onClick={() => setSelectedProductId(null)}>
              Back
            </Button>
            <Button onClick={handleExportExcel} disabled={!selectedProductId || !activeScenario}>
              Export Comparison
            </Button>
          </div>
        </CardFooter>
      </Card>
    </div>
  );
};

