  const handleToggleStorage = async () => {
    setIsChanging(true);
    try {
      const newMode = storageMode === 'cloud' ? 'local' : 'cloud';
      await setStorageMode(newMode);
    } finally {
      setIsChanging(false);
    }
  };

  return (
    <div className="fixed top-4 right-4 z-50 flex items-center gap-2">
