import * as React from "react";
import { cn } from "../../lib/utils";

export interface InputProps
  extends React.InputHTMLAttributes<HTMLInputElement> {
  onValueChange?: (value: string) => void;
}

// Fixed Input component that correctly handles numbers and focus
const Input = React.forwardRef<HTMLInputElement, InputProps>(
  ({ className, type, value, onChange, onValueChange, ...props }, ref) => {
    // Convert 0 to empty string for display only if it's a number type input
    const displayValue = type === "number" && value === 0 ? "" : value;
    
    // Use a local ref if none is provided
    const inputRef = React.useRef<HTMLInputElement>(null);
    const resolvedRef = ref || inputRef;
    
    // Track focus state to prevent cursor issues
    const [focused, setFocused] = React.useState(false);
    
    // Log values for debugging
    React.useEffect(() => {
      console.log("Input value:", value, "Display value:", displayValue);
    }, [value, displayValue]);
    
    return (
      <input
        type={type}
        className={cn(
          "flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background file:border-0 file:bg-transparent file:text-sm file:font-medium placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50",
          className
        )}
        ref={resolvedRef}
        value={displayValue}
        onFocus={(e) => {
          setFocused(true);
          props.onFocus?.(e);
        }}
        onBlur={(e) => {
          setFocused(false);
          props.onBlur?.(e);
        }}
        onChange={(e) => {
          const newValue = e.target.value;
          
          // Handle number input type specially
          if (type === "number") {
            // Allow empty input (will be treated as 0 in parent component)
            // Don't convert to number here to avoid cursor jumps
            if (newValue === "") {
              console.log("Empty input detected");
              onChange?.(e);
              onValueChange?.(newValue);
              return;
            }
            
            // For numeric inputs, validate and clean the input
            if (!/^-?\d*\.?\d*$/.test(newValue)) {
              // Invalid number format, don't update
              return;
            }
          }
          
          onChange?.(e);
          onValueChange?.(newValue);
        }}
        {...props}
      />
    );
  }
);

Input.displayName = "Input";

export { Input };

import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from './card';
import { Input } from './input';
import { Button } from './button';
import useStore from '../../store/useStore';
import type { Product } from '../../types';

interface QuickJumpProps {
  className?: string;
  onNavigate: (path: string) => void;
  onSelectTab?: (tabId: string) => void;
}

// Common product tab destinations
const PRODUCT_TABS = [
  { label: 'Executive Dashboard', tabId: 'dashboard' },
  { label: 'Revenue Forecast', tabId: 'revenue' },
  { label: 'Cost Forecast', tabId: 'costs' },
  { label: '12-Week Forecast', tabId: 'forecast' },
  { label: 'Actuals Tracker', tabId: 'actuals' },
  { label: 'Marketing Analytics', tabId: 'marketing' },
  { label: 'Financial Projections', tabId: 'financials' },
  { label: 'Long-term Projections', tabId: 'longterm' },
  { label: 'Seasonal Analysis', tabId: 'seasonal' },
  { label: 'Scenario Modeling', tabId: 'scenarios' },
  { label: 'Risk Assessment', tabId: 'risk' },
];

// Common app destinations
const COMMON_DESTINATIONS = [
  { label: 'Home', path: '/' },
];

export function QuickJump({ className, onNavigate, onSelectTab }: QuickJumpProps) {
  const { products, currentProductId } = useStore();
  const [searchQuery, setSearchQuery] = useState('');
  
  // Get current product
  const currentProduct = currentProductId 
    ? products.find(p => p.info.id === currentProductId) 
    : null;
  
  // Create a list of all possible destinations
  const allDestinations = [
    ...COMMON_DESTINATIONS,
    // Only include product tabs if we're currently viewing a product and onSelectTab is provided
    ...(currentProduct && onSelectTab ? PRODUCT_TABS.map(tab => ({
      ...tab,
      isTab: true,
    })) : []),
    // Add other products (excluding current product)
    ...products
      .filter(p => p.info.id !== currentProductId)
      .map(product => ({
        label: product.info.name,
        path: `/product/${product.info.id}`,
        isTab: false,
      }))
  ];
  
  // Filter destinations based on search query
  const filteredDestinations = searchQuery
    ? allDestinations.filter(dest => 
        dest.label.toLowerCase().includes(searchQuery.toLowerCase())
      )
    : allDestinations;
  
  const handleInputChange = (e: unknown) => {
    setSearchQuery(e.target.value);
  };
  
  // Handle item click - either navigate to path or select tab
  const handleItemClick = (item: unknown) => {
    if (item.isTab && onSelectTab) {
      // It's a tab and we have a tab handler
      onSelectTab(item.tabId);
    } else if (item.path) {
      // It's a path, use navigation handler
      onNavigate(item.path);
    }
  };
  
  return (
    <Card className={className}>
      <CardHeader>
        <CardTitle className="text-lg">Quick Jump</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          <Input
            placeholder="Search destinations..."
            value={searchQuery}
            onChange={handleInputChange}
            className="w-full"
          />
          
          <div className="h-[200px] overflow-auto rounded-md border p-2">
            <div className="space-y-1">
              {filteredDestinations.map((item, index) => (
                <Button
                  key={`${item.path || item.tabId}-${index}`}
                  variant="ghost"
                  className={`w-full justify-start text-left text-sm px-2 py-1 h-auto ${item.isTab ? 'text-blue-600' : ''}`}
                  onClick={() => handleItemClick(item)}
                >
                  {item.label}
                  {item.isTab && <span className="ml-1 text-xs opacity-70">(tab)</span>}
                </Button>
              ))}
              
              {filteredDestinations.length === 0 && (
                <p className="text-sm text-gray-500 px-2 py-4 text-center">
                  No matching destinations found
                </p>
              )}
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

import React, { useState } from 'react';
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from './card';
import { Button } from './button';
import { Progress } from './progress';
import useStore from '../../store/useStore';
import type { Product, RevenueMetrics } from '../../types';
import { ChevronUp, ChevronDown } from 'lucide-react';

interface SetupWizardProps {
  className?: string;
  onNavigate: (path: string) => void;
  onSelectTab?: (tabId: string) => void;
}

// Define a type for the step
interface SetupStep {
  title: string;
  description: string;
  tabId?: string;
  path?: string;
  isComplete: boolean;
}

export function SetupWizard({ className, onNavigate, onSelectTab }: SetupWizardProps) {
  const { products, currentProductId } = useStore();
  const [isCollapsed, setIsCollapsed] = useState(false);
  
  // Find current product
  const currentProduct = currentProductId 
    ? products.find(p => p.info.id === currentProductId) 
    : null;
  
  // Function to check if at least one revenue field has a value
  const hasAnyRevenueField = (revenueMetrics?: Partial<RevenueMetrics> | null): boolean => {
    if (!revenueMetrics) return false;
    
    // Check if any of the revenue fields have a non-zero value
    return (
      (revenueMetrics.ticketPrice !== undefined && revenueMetrics.ticketPrice > 0) ||
      (revenueMetrics.fbSpend !== undefined && revenueMetrics.fbSpend > 0) ||
      (revenueMetrics.merchandiseSpend !== undefined && revenueMetrics.merchandiseSpend > 0) ||
      (revenueMetrics.digitalPrice !== undefined && revenueMetrics.digitalPrice > 0)
    );
  };
  
  // Setup wizard steps with validation
  const setupSteps = [
    {
      title: 'Product Info',
      description: 'Set up your product details',
      tabId: 'dashboard',
      isComplete: Boolean(currentProduct?.info?.name && currentProduct?.info?.description),
    },
    {
      title: 'Revenue Forecasting',
      description: 'Set up your revenue model',
      tabId: 'revenue',
      isComplete: hasAnyRevenueField(currentProduct?.revenueMetrics),
    },
    {
      title: 'Cost Setup',
      description: 'Configure your costs',
      tabId: 'costs',
      isComplete: Boolean(
        currentProduct?.costMetrics?.eventCosts?.length || 
        currentProduct?.costMetrics?.staffRoles?.length
      ),
    },
    {
      title: 'Risk Assessment',
      description: 'Identify potential risks',
      tabId: 'risk',
      isComplete: Boolean(currentProduct?.risks?.length),
    },
  ];
  
  // Calculate overall setup completion percentage
  const completedSteps = setupSteps.filter(step => step.isComplete).length;
  const completionPercentage = (completedSteps / setupSteps.length) * 100;
  
  if (!currentProduct) {
    return null;
  }
  
  // Enhanced step click handler with improved error handling and feedback
  const handleStepClick = (step: SetupStep) => {
    console.log('Step clicked:', step);
    
    // Don't automatically change tabs - only navigate if explicitly requested by the user
    if (step.path && onNavigate) {
      onNavigate(step.path);
    }
    
    // We're disabling automatic tab selection to prevent unwanted tab switching
    // If we need this functionality later, we can re-enable it
  };
  
  const toggleCollapse = () => {
    setIsCollapsed(!isCollapsed);
  };
  
  return (
    <Card className={className}>
      <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
        <div>
          <CardTitle className="text-lg">Setup Wizard</CardTitle>
          <CardDescription>
            {completedSteps === setupSteps.length
              ? 'All setup steps completed!'
              : `${completedSteps} of ${setupSteps.length} steps completed`}
          </CardDescription>
        </div>
        <Button 
          variant="ghost" 
          size="sm" 
          onClick={toggleCollapse}
          className="h-8 w-8 p-0"
        >
          {isCollapsed ? <ChevronDown className="h-4 w-4" /> : <ChevronUp className="h-4 w-4" />}
        </Button>
      </CardHeader>
      
      {!isCollapsed && (
        <>
          <CardContent className="space-y-4 pt-2">
            <Progress value={completionPercentage} className="h-2" />
            
            <div className="space-y-2">
              {setupSteps.map((step, index) => (
                <div 
                  key={index}
                  className="flex items-center justify-between border rounded-md p-3"
                >
                  <div>
                    <h3 className="font-medium">{step.title}</h3>
                    <p className="text-sm text-gray-500">{step.description}</p>
                  </div>
                  <div className="flex items-center gap-2">
                    {step.isComplete ? (
                      <div className="h-4 w-4 rounded-full bg-green-500" />
                    ) : (
                      <div className="h-4 w-4 rounded-full bg-gray-200" />
                    )}
                    <Button 
                      variant="outline" 
                      size="sm"
                      onClick={() => handleStepClick(step)}
                      className="hover:bg-blue-50 active:bg-blue-100 transition-colors font-medium"
                    >
                      {step.isComplete ? 'Edit' : 'Setup'}
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
          <CardFooter className="justify-between">
            <p className="text-sm text-gray-500">
              {completionPercentage >= 100 
                ? 'Product fully configured!' 
                : 'Complete all steps to fully configure your product'}
            </p>
          </CardFooter>
        </>
      )}
    </Card>
  );
}

import React, { createContext, useContext, useState, useEffect } from 'react';
import { 
  signInWithEmailAndPassword, 
  createUserWithEmailAndPassword, 
  signOut as firebaseSignOut, 
  onAuthStateChanged,
  signInAnonymously as firebaseSignInAnonymously,
  sendPasswordResetEmail,
  sendEmailVerification,
  GoogleAuthProvider,
  signInWithPopup,
  linkWithCredential,
  EmailAuthProvider,
  updateProfile,
  User, 
  UserCredential,
  Auth
} from 'firebase/auth';
import { auth, isFirebaseInitialized } from '../lib/firebase';
import type { FallbackAuth } from '../lib/firebase';

interface AuthContextType {
  currentUser: User | null;
  isLoading: boolean;
  signUp: (email: string, password: string) => Promise<UserCredential>;
  signIn: (email: string, password: string) => Promise<UserCredential>;
  signInWithGoogle: () => Promise<UserCredential>;
  signInAnonymously: () => Promise<UserCredential>;
  logout: () => Promise<void>;
  resetPassword: (email: string) => Promise<void>;
  sendVerificationEmail: () => Promise<void>;
  linkAnonymousAccount: (email: string, password: string) => Promise<UserCredential>;
  isAuthenticated: boolean;
  isAnonymous: boolean;
  isEmailVerified: boolean;
}

const AuthContext = createContext<AuthContextType | null>(null);

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

interface AuthProviderProps {
  children: React.ReactNode;
}

// Create a mock UserCredential for fallback mode
const createMockUserCredential = (): UserCredential => {
  return {
    user: null as any,
    providerId: null as any,
    operationType: null as any
  } as UserCredential;
};

export const AuthProvider = ({ children }: AuthProviderProps) => {
  const [currentUser, setCurrentUser] = useState<User | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  
  // Track if Firebase is available - this provider works with both real and fallback auth
  const isFirebaseAvailable = isFirebaseInitialized();

  useEffect(() => {
    // Only attempt to observe auth state if Firebase is initialized
    let unsubscribe = () => {};
    
    try {
      // Set up the auth state listener
      unsubscribe = onAuthStateChanged(auth as Auth, (user) => {
        setCurrentUser(user);
        setIsLoading(false);
      });
    } catch (error) {
      console.error('Error setting up auth listener:', error);
      // In case of error, just mark as done loading
      setIsLoading(false);
    }

    return unsubscribe;
  }, []);

  // Create fallback/dummy implementations for all auth methods
  // These will be used when Firebase is not initialized
  const dummyAuth = {
    // Sign up with email and password - fallback
    signUp: async (email: string, password: string) => {
      console.log('Fallback auth: Sign up attempt with', email);
      return createMockUserCredential();
    },

    // Sign in with email and password - fallback 
    signIn: async (email: string, password: string) => {
      console.log('Fallback auth: Sign in attempt with', email);
      return createMockUserCredential();
    },

    // Sign in with Google - fallback
    signInWithGoogle: async () => {
      console.log('Fallback auth: Sign in with Google attempt');
      return createMockUserCredential();
    },

    // Sign in anonymously - fallback
    signInAnonymously: async () => {
      console.log('Fallback auth: Anonymous sign in attempt');
      return createMockUserCredential();
    },

    // Sign out - fallback
    logout: async () => {
      console.log('Fallback auth: Logout attempt');
      return;
    },

    // Reset password - fallback
    resetPassword: async (email: string) => {
      console.log('Fallback auth: Password reset attempt for', email);
      return;
    },

    // Send email verification - fallback
    sendVerificationEmail: async () => {
      console.log('Fallback auth: Verification email attempt');
      return;
    },

    // Link anonymous account - fallback
    linkAnonymousAccount: async (email: string, password: string) => {
      console.log('Fallback auth: Link anonymous account attempt with', email);
      return createMockUserCredential();
    }
  };

  // Real Firebase auth functions - used when Firebase is initialized
  const firebaseAuthFunctions = {
    // Sign up with email and password
    signUp: (email: string, password: string) => {
      return createUserWithEmailAndPassword(auth as Auth, email, password);
    },

    // Sign in with email and password
    signIn: (email: string, password: string) => {
      return signInWithEmailAndPassword(auth as Auth, email, password);
    },

    // Sign in with Google
    signInWithGoogle: async () => {
      const provider = new GoogleAuthProvider();
      return signInWithPopup(auth as Auth, provider);
    },

    // Sign in anonymously
    signInAnonymously: () => {
      return firebaseSignInAnonymously(auth as Auth);
    },

    // Sign out
    logout: () => {
      return firebaseSignOut(auth as Auth);
    },

    // Reset password
    resetPassword: (email: string) => {
      return sendPasswordResetEmail(auth as Auth, email);
    },

    // Send email verification
    sendVerificationEmail: async () => {
      if (!currentUser) {
        throw new Error('No user logged in');
      }
      return sendEmailVerification(currentUser);
    },

    // Link anonymous account with email/password
    linkAnonymousAccount: async (email: string, password: string) => {
      if (!currentUser) {
        throw new Error('No user logged in');
      }
      
      if (!currentUser.isAnonymous) {
        throw new Error('User is not anonymous');
      }
      
      const credential = EmailAuthProvider.credential(email, password);
      return linkWithCredential(currentUser, credential);
    }
  };

  // Choose between real and fallback implementations based on Firebase initialization
  const authImplementation = isFirebaseAvailable ? firebaseAuthFunctions : dummyAuth;

  const value = {
    currentUser,
    isLoading,
    ...authImplementation,
    isAuthenticated: !!currentUser,
    isAnonymous: currentUser?.isAnonymous || false,
    isEmailVerified: currentUser?.emailVerified || false
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;

import React, { createContext, useContext, useState, useEffect } from 'react';
import { 
  User, 
  signInAnonymously as localSignInAnonymously,
  getCurrentUser, 
  signOut as localSignOut
} from '../lib/localStorageService';
import { generateId } from '../lib/localStorageService';

interface AuthContextType {
  currentUser: User | null;
  isLoading: boolean;
  signUp: (email: string, password: string) => Promise<User>;
  signIn: (email: string, password: string) => Promise<User>;
  signInWithGoogle: () => Promise<User>;
  signInAnonymously: () => Promise<User>;
  logout: () => Promise<void>;
  resetPassword: (email: string) => Promise<void>;
  sendVerificationEmail: () => Promise<void>;
  linkAnonymousAccount: (email: string, password: string) => Promise<User>;
  isAuthenticated: boolean;
  isAnonymous: boolean;
  isEmailVerified: boolean;
}

const LocalAuthContext = createContext<AuthContextType | null>(null);

export const useAuth = () => {
  const context = useContext(LocalAuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

interface AuthProviderProps {
  children: React.ReactNode;
}

// Mock user management
interface MockUsers {
  [email: string]: {
    id: string;
    email: string; // Email is required and not null
    password: string;
    displayName: string | null;
    isAnonymous: boolean;
    emailVerified: boolean;
  };
}

// Simulate a user database in localStorage
const getUsersFromStorage = (): MockUsers => {
  try {
    const users = localStorage.getItem('mock-users');
    return users ? JSON.parse(users) : {};
  } catch (error) {
    console.error('Error loading mock users:', error);
    return {};
  }
};

const saveUsersToStorage = (users: MockUsers): void => {
  localStorage.setItem('mock-users', JSON.stringify(users));
};

export const LocalAuthProvider = ({ children }: AuthProviderProps) => {
  const [currentUser, setCurrentUser] = useState<User | null>(getCurrentUser());
  const [isLoading, setIsLoading] = useState(false);

  // Auth methods for the local implementation
  const authMethods = {
    // Sign up with email/password
    signUp: async (email: string, password: string): Promise<User> => {
      setIsLoading(true);
      try {
        const users = getUsersFromStorage();
        
        if (users[email]) {
          throw new Error('Email already in use');
        }
        
        const newUser = {
          id: generateId(),
          email,
          password,
          displayName: email.split('@')[0],
          isAnonymous: false,
          emailVerified: false
        };
        
        users[email] = newUser;
        saveUsersToStorage(users);
        
        // Create and set the current user (without the password)
        const user: User = {
          id: newUser.id,
          email: newUser.email,
          displayName: newUser.displayName,
          isAnonymous: newUser.isAnonymous,
          emailVerified: newUser.emailVerified
        };
        
        setCurrentUser(user);
        return user;
      } finally {
        setIsLoading(false);
      }
    },
    
    // Sign in with email/password
    signIn: async (email: string, password: string): Promise<User> => {
      setIsLoading(true);
      try {
        const users = getUsersFromStorage();
        const user = users[email];
        
        if (!user) {
          throw new Error('User not found');
        }
        
        if (user.password !== password) {
          throw new Error('Invalid password');
        }
        
        // Create and set the current user (without the password)
        const authUser: User = {
          id: user.id,
          email: user.email,
          displayName: user.displayName,
          isAnonymous: user.isAnonymous,
          emailVerified: user.emailVerified
        };
        
        setCurrentUser(authUser);
        return authUser;
      } finally {
        setIsLoading(false);
      }
    },
    
    // Sign in with Google (mock)
    signInWithGoogle: async (): Promise<User> => {
      setIsLoading(true);
      try {
        // Create a fake Google user with a guaranteed email
        const email = `google-user-${generateId().substring(0, 6)}@example.com`;
        const googleUser: User = {
          id: generateId(),
          email,
          displayName: 'Google User',
          isAnonymous: false,
          emailVerified: true
        };
        
        setCurrentUser(googleUser);
        
        // Also save to our mock database
        const users = getUsersFromStorage();
        users[email] = {
          ...googleUser,
          password: generateId() // random password that user doesn't know
        };
        saveUsersToStorage(users);
        
        return googleUser;
      } finally {
        setIsLoading(false);
      }
    },
    
    // Sign in anonymously
    signInAnonymously: async (): Promise<User> => {
      setIsLoading(true);
      try {
        const user = localSignInAnonymously();
        setCurrentUser(user);
        return user;
      } finally {
        setIsLoading(false);
      }
    },
    
    // Sign out
    logout: async (): Promise<void> => {
      setIsLoading(true);
      try {
        localSignOut();
        setCurrentUser(null);
      } finally {
        setIsLoading(false);
      }
    },
    
    // Reset password (mock)
    resetPassword: async (email: string): Promise<void> => {
      setIsLoading(true);
      try {
        const users = getUsersFromStorage();
        
        if (!users[email]) {
          throw new Error('User not found');
        }
        
        // In a real app, we would send an email here
        console.log(`Password reset requested for: ${email}`);
      } finally {
        setIsLoading(false);
      }
    },
    
    // Send verification email (mock)
    sendVerificationEmail: async (): Promise<void> => {
      setIsLoading(true);
      try {
        if (!currentUser || !currentUser.email) {
          throw new Error('No user logged in or user has no email');
        }
        
        // In a real app, we would send an email here
        console.log(`Verification email sent to: ${currentUser.email}`);
        
        // For demo purposes, let's just mark the user as verified
        const users = getUsersFromStorage();
        if (currentUser.email && users[currentUser.email]) {
          users[currentUser.email].emailVerified = true;
          saveUsersToStorage(users);
          
          // Update current user
          setCurrentUser({
            ...currentUser,
            emailVerified: true
          });
        }
      } finally {
        setIsLoading(false);
      }
    },
    
    // Link anonymous account
    linkAnonymousAccount: async (email: string, password: string): Promise<User> => {
      setIsLoading(true);
      try {
        if (!currentUser) {
          throw new Error('No user logged in');
        }
        
        if (!currentUser.isAnonymous) {
          throw new Error('User is not anonymous');
        }
        
        const users = getUsersFromStorage();
        
        if (users[email]) {
          throw new Error('Email already in use');
        }
        
        // Create a new non-anonymous user
        const linkedUser: User = {
          id: currentUser.id, // Keep the same ID
          email, // Email is guaranteed to be a string here
          displayName: email.split('@')[0],
          isAnonymous: false,
          emailVerified: false
        };
        
        // Save to our mock database
        users[email] = {
          ...linkedUser,
          password
        };
        saveUsersToStorage(users);
        
        // Update current user
        setCurrentUser(linkedUser);
        return linkedUser;
      } finally {
        setIsLoading(false);
      }
    }
  };

  // Auto sign in anonymously on load if no user is present
  useEffect(() => {
    if (!currentUser) {
      authMethods.signInAnonymously();
    }
  }, []);

  const value = {
    currentUser,
    isLoading,
    ...authMethods,
    isAuthenticated: !!currentUser,
    isAnonymous: currentUser?.isAnonymous || false,
    isEmailVerified: currentUser?.emailVerified || false
  };

  return (
    <LocalAuthContext.Provider value={value}>
      {children}
    </LocalAuthContext.Provider>
  );
};

export default LocalAuthContext;

import React, { createContext, useContext, useEffect, useState } from 'react';
import { doc, onSnapshot, Firestore } from 'firebase/firestore';
import { getDb, isFirebaseInitialized } from '../lib/firebase';

// Define the NetworkStatus type
interface NetworkStatus {
  isOnline: boolean;
  connectionType: string | null;
  isConnectionPending: boolean;
}

// Define the context with default values
const NetworkStatusContext = createContext<NetworkStatus & {
  checkConnection: () => void;
}>({
  isOnline: true,
  connectionType: null,
  isConnectionPending: false,
  checkConnection: () => {}
});

// Hook to access the network status context
export const useNetworkStatus = () => useContext(NetworkStatusContext);

// Provider component for network status
export const NetworkStatusProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  // State for overall network status
  const [isOnline, setIsOnline] = useState<boolean>(navigator.onLine);
  // State for connection type (e.g., 4g, wifi, etc.)
  const [connectionType, setConnectionType] = useState<string | null>(null);
  // State to track if we're still determining connection status
  const [isConnectionPending, setIsConnectionPending] = useState<boolean>(true);

  // Update connection type when available
  useEffect(() => {
    const updateConnectionType = () => {
      if ('connection' in navigator && navigator.connection) {
        // @ts-expect-error - Navigator connection API not fully typed
        const { effectiveType } = navigator.connection;
        setConnectionType(effectiveType);
      }
    };

    // Get initial connection type
    updateConnectionType();

    // Listen for connection changes
    // @ts-expect-error - Navigator connection API not fully typed
    if ('connection' in navigator && navigator.connection?.addEventListener) {
      // @ts-expect-error - Navigator connection API not fully typed
      navigator.connection.addEventListener('change', updateConnectionType);
      return () => {
        // @ts-expect-error - Navigator connection API not fully typed
        navigator.connection.removeEventListener('change', updateConnectionType);
      };
    }
  }, []);

  // Monitor navigator.onLine status
  useEffect(() => {
    const handleOnline = () => {
      setIsOnline(true);
      // When we reconnect, we should re-check if Firestore is available
      checkFirestoreConnection();
    };

    const handleOffline = () => {
      setIsOnline(false);
      setIsConnectionPending(false);
    };

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  // Function to check Firestore connection
  const checkFirestoreConnection = () => {
    setIsConnectionPending(true);
    
    // Only check Firestore if it's initialized
    if (!isFirebaseInitialized()) {
      setIsConnectionPending(false);
      return;
    }
    
    try {
      // Attempt to connect to Firestore's special .info/connected document
      // This will tell us if we can reach Firebase's servers
      const db = getDb() as unknown as Firestore;
      
      const unsubscribe = onSnapshot(
        doc(db, '.info/connected'),
        (snapshot) => {
          const connected = snapshot.exists() && snapshot.data()?.connected === true;
          
          if (connected) {
            setIsConnectionPending(false);
          } else {
            // If not connected after a timeout, consider it failed
            setTimeout(() => {
              setIsConnectionPending(false);
            }, 5000);
          }
        },
        (error) => {
          console.error('Firestore connection error:', error);
          setIsConnectionPending(false);
        }
      );

      // Clean up the listener after a timeout
      setTimeout(() => {
        unsubscribe();
      }, 10000);
    } catch (error) {
      console.error('Error checking Firestore connection:', error);
      setIsConnectionPending(false);
    }
  };

  // Initial check
  useEffect(() => {
    if (isOnline) {
      checkFirestoreConnection();
    }
  }, []);

  // Function to manually check connection
  const checkConnection = () => {
    if (navigator.onLine) {
      checkFirestoreConnection();
    } else {
      setIsOnline(false);
      setIsConnectionPending(false);
    }
  };

  return (
    <NetworkStatusContext.Provider 
      value={{ 
        isOnline, 
        connectionType, 
        isConnectionPending, 
        checkConnection 
      }}
    >
      {children}
    </NetworkStatusContext.Provider>
  );
};

export default NetworkStatusProvider;

import React, { createContext, useContext, useState, useCallback } from 'react';

export type NotificationType = 'info' | 'success' | 'warning' | 'error';

export interface Notification {
  id: string;
  message: string;
  type: NotificationType;
  title?: string;
  autoClose?: boolean;
  duration?: number;
}

interface NotificationContextType {
  notifications: Notification[];
  addNotification: (notification: Omit<Notification, 'id'>) => string;
  updateNotification: (id: string, notification: Partial<Notification>) => void;
  removeNotification: (id: string) => void;
  clearNotifications: () => void;
}

const NotificationContext = createContext<NotificationContextType>({
  notifications: [],
  addNotification: () => '',
  updateNotification: () => {},
  removeNotification: () => {},
  clearNotifications: () => {},
});

export const useNotifications = () => useContext(NotificationContext);

interface NotificationProviderProps {
  children: React.ReactNode;
}

export function NotificationProvider({ children }: NotificationProviderProps) {
  const [notifications, setNotifications] = useState<Notification[]>([]);

  const addNotification = useCallback((notification: Omit<Notification, 'id'>) => {
    const id = `notification-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
    const newNotification: Notification = {
      id,
      autoClose: notification.type !== 'error', // Auto close everything except errors
      duration: notification.type === 'error' ? 0 : 5000, // 5 seconds for non-errors, 0 (no auto-close) for errors
      ...notification,
    };
    
    setNotifications((prev: Notification[]) => [...prev, newNotification]);
    
    // Auto close notification if specified
    if (newNotification.autoClose && newNotification.duration) {
      setTimeout(() => {
        removeNotification(id);
      }, newNotification.duration);
    }
    
    return id;
  }, []);

  const updateNotification = useCallback((id: string, updates: Partial<Notification>) => {
    setNotifications((prev: Notification[]) =>
      prev.map((notification: Notification) =>
        notification.id === id
          ? { ...notification, ...updates }
          : notification
      )
    );
  }, []);

  const removeNotification = useCallback((id: string) => {
    setNotifications((prev: Notification[]) => prev.filter((notification: Notification) => notification.id !== id));
  }, []);

  const clearNotifications = useCallback(() => {
    setNotifications([]);
  }, []);

  return (
    <NotificationContext.Provider
      value={{
        notifications,
        addNotification,
        updateNotification,
        removeNotification,
        clearNotifications,
      }}
    >
      {children}
    </NotificationContext.Provider>
  );
}

