      week.channelPerformance.forEach(channel => {
        allChannelData.push({
          week: week.week,
          date: week.date,
          channelId: channel.channelId,
          spend: channel.spend || 0,
          revenue: channel.revenue || 0,
          impressions: channel.impressions || 0,
          clicks: channel.clicks || 0,
