/**
 * Helper to download a file to the user's device
 */
export function downloadFile(content: string | Blob, fileName: string, mimeType?: string): void {
  let blob: Blob;
  
  // Convert string content to blob if needed
  if (typeof content === 'string') {
    blob = new Blob([content], { type: mimeType });
  } else {
    blob = content;
  }
  
  // Create download link
  const url = URL.createObjectURL(blob);
  const link = document.createElement('a');
  link.href = url;
  link.download = fileName;
  document.body.appendChild(link);
  link.click();
  
  // Clean up
  setTimeout(() => {
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  }, 100);
}

// Add the new exportScenarioComparison function
export const exportScenarioComparison = (
  product: Product, 
  baselineData: WeeklyProjection[], 
  scenarioData: WeeklyProjection[],
  scenarioName: string
) => {
  // Define the columns for export
  const formatCurrency = (value: unknown) => `$${Number(value).toLocaleString()}`;
  const formatPercentage = (value: unknown) => `${Number(value).toFixed(2)}%`;
  const formatNumber = (value: unknown) => Number(value).toLocaleString();
  
  const headers = [
    { key: 'week', label: 'Week' },
    { key: 'baseline_revenue', label: 'Baseline Revenue', format: formatCurrency },
    { key: 'scenario_revenue', label: 'Scenario Revenue', format: formatCurrency },
    { key: 'revenue_diff', label: 'Revenue Difference', format: formatCurrency },
    { key: 'revenue_diff_pct', label: 'Revenue Diff %', format: formatPercentage },
    { key: 'baseline_cost', label: 'Baseline Cost', format: formatCurrency },
    { key: 'scenario_cost', label: 'Scenario Cost', format: formatCurrency },
    { key: 'cost_diff', label: 'Cost Difference', format: formatCurrency },
    { key: 'cost_diff_pct', label: 'Cost Diff %', format: formatPercentage },
    { key: 'baseline_profit', label: 'Baseline Profit', format: formatCurrency },
    { key: 'scenario_profit', label: 'Scenario Profit', format: formatCurrency },
    { key: 'profit_diff', label: 'Profit Difference', format: formatCurrency },
    { key: 'profit_diff_pct', label: 'Profit Diff %', format: formatPercentage },
    { key: 'baseline_attendance', label: 'Baseline Attendance', format: formatNumber },
    { key: 'scenario_attendance', label: 'Scenario Attendance', format: formatNumber },
    { key: 'attendance_diff_pct', label: 'Attendance Diff %', format: formatPercentage }
  ];

  // Generate weekly data for export
  const dataset = baselineData.map((baseline, index) => {
    const scenario = scenarioData[index];
    
    // Calculate revenues
    const baselineRevenue = baseline.ticketRevenue + baseline.fbRevenue + 
                           baseline.merchandiseRevenue + baseline.digitalRevenue;
    
    const scenarioRevenue = scenario.ticketRevenue + scenario.fbRevenue + 
                          scenario.merchandiseRevenue + scenario.digitalRevenue;
    
    // Calculate costs
    const baselineCost = baseline.marketingCosts + baseline.staffingCosts + 
                        baseline.eventCosts + baseline.setupCosts;
                        
    const scenarioCost = scenario.marketingCosts + scenario.staffingCosts + 
                       scenario.eventCosts + scenario.setupCosts;
    
    // Calculate profits
    const baselineProfit = baselineRevenue - baselineCost;
    const scenarioProfit = scenarioRevenue - scenarioCost;
    
    // Calculate differences
    const revenueDiff = scenarioRevenue - baselineRevenue;
    const revenueDiffPct = baselineRevenue !== 0 ? (revenueDiff / baselineRevenue) * 100 : 0;
    
    const costDiff = scenarioCost - baselineCost;
    const costDiffPct = baselineCost !== 0 ? (costDiff / baselineCost) * 100 : 0;
    
    const profitDiff = scenarioProfit - baselineProfit;
    const profitDiffPct = baselineProfit !== 0 ? (profitDiff / baselineProfit) * 100 : 0;
    
    const attendanceDiffPct = baseline.footTraffic !== 0 
      ? ((scenario.footTraffic - baseline.footTraffic) / baseline.footTraffic) * 100 
      : 0;
    
    return {
      week: baseline.week,
      baseline_revenue: baselineRevenue,
      scenario_revenue: scenarioRevenue,
      revenue_diff: revenueDiff,
      revenue_diff_pct: revenueDiffPct,
      baseline_cost: baselineCost,
      scenario_cost: scenarioCost,
      cost_diff: costDiff,
      cost_diff_pct: costDiffPct,
      baseline_profit: baselineProfit,
      scenario_profit: scenarioProfit,
      profit_diff: profitDiff,
      profit_diff_pct: profitDiffPct,
      baseline_attendance: baseline.footTraffic,
      scenario_attendance: scenario.footTraffic,
      attendance_diff_pct: attendanceDiffPct
    };
  });

  // Add summary row
  const baselineTotalRevenue = dataset.reduce((sum, row) => sum + row.baseline_revenue, 0);
  const scenarioTotalRevenue = dataset.reduce((sum, row) => sum + row.scenario_revenue, 0);
  const baselineTotalCost = dataset.reduce((sum, row) => sum + row.baseline_cost, 0);
  const scenarioTotalCost = dataset.reduce((sum, row) => sum + row.scenario_cost, 0);
  const baselineTotalProfit = dataset.reduce((sum, row) => sum + row.baseline_profit, 0);
  const scenarioTotalProfit = dataset.reduce((sum, row) => sum + row.scenario_profit, 0);
  const baselineTotalAttendance = dataset.reduce((sum, row) => sum + row.baseline_attendance, 0);
  const scenarioTotalAttendance = dataset.reduce((sum, row) => sum + row.scenario_attendance, 0);
  
  const totalRevenueDiff = scenarioTotalRevenue - baselineTotalRevenue;
  const totalRevenueDiffPct = baselineTotalRevenue !== 0 ? (totalRevenueDiff / baselineTotalRevenue) * 100 : 0;
  const totalCostDiff = scenarioTotalCost - baselineTotalCost;
  const totalCostDiffPct = baselineTotalCost !== 0 ? (totalCostDiff / baselineTotalCost) * 100 : 0;
  const totalProfitDiff = scenarioTotalProfit - baselineTotalProfit;
  const totalProfitDiffPct = baselineTotalProfit !== 0 ? (totalProfitDiff / baselineTotalProfit) * 100 : 0;
  const totalAttendanceDiffPct = baselineTotalAttendance !== 0 
    ? ((scenarioTotalAttendance - baselineTotalAttendance) / baselineTotalAttendance) * 100 
    : 0;
    
  dataset.push({
    week: 'TOTAL' as any,
    baseline_revenue: baselineTotalRevenue,
    scenario_revenue: scenarioTotalRevenue,
    revenue_diff: totalRevenueDiff,
    revenue_diff_pct: totalRevenueDiffPct,
    baseline_cost: baselineTotalCost,
    scenario_cost: scenarioTotalCost,
    cost_diff: totalCostDiff,
    cost_diff_pct: totalCostDiffPct,
    baseline_profit: baselineTotalProfit,
    scenario_profit: scenarioTotalProfit,
    profit_diff: totalProfitDiff,
    profit_diff_pct: totalProfitDiffPct,
    baseline_attendance: baselineTotalAttendance,
    scenario_attendance: scenarioTotalAttendance,
    attendance_diff_pct: totalAttendanceDiffPct
  });

  // Export the data as Excel
  return exportForExcel(
    dataset,
    headers,
    { 
      fileName: `${product.info.name}_${scenarioName}_Comparison`,
      includeTimestamp: true
    }
  );
};

// Simplified Firebase stub for local-only usage
// This provides mock implementations that don't depend on actual Firebase

// Create mock implementations of Firebase interfaces
export interface FallbackFirestore {
  collection: (path: string) => any;
  doc: (path: string) => any;
  type: 'firestore';
}

export interface FallbackAuth {
  currentUser: null;
  onAuthStateChanged: (callback: (user: null) => void) => void;
  signInAnonymously: () => Promise<{user: {uid: string}}>;
  signOut: () => Promise<void>;
  type: 'auth';
}

// Create a simple local-only implementation of Firestore
const createFallbackFirestore = (): FallbackFirestore => ({
  collection: () => ({
    add: (data: any) => Promise.resolve({ id: 'local-' + Date.now() }),
    doc: (id: string) => ({
      get: () => Promise.resolve({ exists: false, data: () => ({}) }),
      set: (data: any) => Promise.resolve(),
      update: (data: any) => Promise.resolve(),
      delete: () => Promise.resolve()
    }),
    get: () => Promise.resolve({ docs: [] })
  }),
  doc: (path: string) => ({
    get: () => Promise.resolve({ exists: false, data: () => ({}) }),
    set: (data: any) => Promise.resolve(),
    update: (data: any) => Promise.resolve(),
    delete: () => Promise.resolve()
  }),
  type: 'firestore'
});

// Create a simple local-only implementation of Auth
const createFallbackAuth = (): FallbackAuth => ({
  currentUser: null,
  onAuthStateChanged: (callback) => {
    callback(null);
    return () => {};
  },
  signInAnonymously: () => Promise.resolve({ user: { uid: 'anonymous-' + Date.now() } }),
  signOut: () => Promise.resolve(),
  type: 'auth'
});

// Create and export the fallback instances
export const db = createFallbackFirestore();
export const auth = createFallbackAuth();
export const analytics = null;

// Stubbed functions to maintain API compatibility
export const isFirebaseInitialized = () => false;
export const isFirebaseConfigAvailable = () => false;
export const initializeFirebase = async () => false;
export const resetFirebase = () => {};
export const getDb = () => db;
export const getFirebaseAuth = () => auth;

import { 
  collection, 
  doc, 
  getDoc, 
  getDocs, 
  setDoc, 
  updateDoc, 
  deleteDoc, 
  query, 
  where,
  serverTimestamp,
  Timestamp,
  DocumentData,
  Firestore
} from 'firebase/firestore';
import { db } from './firebase';
import type { 
  Product, 
  GrowthMetrics, 
  RevenueMetrics, 
  CostMetrics, 
  CustomerMetrics 
} from '../types';

// Firestore Collection Names
const PRODUCTS_COLLECTION = 'products';
const METRICS_COLLECTION = 'metrics';
const PROJECTIONS_COLLECTION = 'projections';
const ACTUALS_COLLECTION = 'actuals';
const MARKETING_KPIS_COLLECTION = 'marketingKpis';

// Error Handling
export function handleFirestoreError(error: unknown): never {
  console.error('Firestore operation failed:', error);
  if (error instanceof Error) {
    throw error;
  }
  throw new Error('An unexpected Firestore error occurred');
}

// Helper to convert Firestore timestamps to Dates in objects
const convertTimestamps = (obj: DocumentData): unknown => {
  const result: unknown = { ...obj };
  Object.keys(result).forEach(key => {
    // Convert timestamps to Date objects
    if (result[key] instanceof Timestamp) {
      result[key] = result[key].toDate();
    }
    // Recursively convert timestamps in nested objects
    else if (typeof result[key] === 'object' && result[key] !== null) {
      result[key] = convertTimestamps(result[key]);
    }
  });
  return result;
};

// PRODUCT OPERATIONS

// Get all products
export async function getProducts(): Promise<Product[]> {
  try {
    const productsRef = collection(db as Firestore, PRODUCTS_COLLECTION);
    const snapshot = await getDocs(productsRef);
    
    const products: Product[] = [];
    snapshot.forEach(doc => {
      const productData = convertTimestamps(doc.data());
      // Make sure product has proper structure
      if (!productData.info) {
        productData.info = { id: doc.id, name: '', description: '', type: 'Experiential Events' };
      }
      if (!productData.info.id) {
        productData.info.id = doc.id;
      }
      products.push(productData as Product);
    });
    
    return products;
  } catch (error) {
    handleFirestoreError(error);
    throw error; // Unreachable due to handleFirestoreError, but keeps TypeScript happy
  }
}

// Create a new product
export async function createProduct(product: Omit<Product, 'id'>): Promise<Product> {
  try {
    // Generate a unique ID
    const productId = crypto.randomUUID();
    
    // Create the product object with the generated ID
    const newProduct = {
      ...product,
      info: {
        ...product.info,
        id: productId,
        createdAt: new Date(),
        updatedAt: new Date()
      }
    } as Product;
    
    // Set the document with the generated ID
    await setDoc(doc(db as Firestore, PRODUCTS_COLLECTION, productId), {
      ...newProduct,
      // Add server timestamp for cloud-based tracking
      _createdAt: serverTimestamp(),
      _updatedAt: serverTimestamp()
    });
    
    return newProduct;
  } catch (error) {
    handleFirestoreError(error);
    throw error;
  }
}

// Update a product
export async function updateProduct(
  id: string,
  updates: Partial<Product>
): Promise<void> {
  try {
    // Reference to the product document
    const productRef = doc(db as Firestore, PRODUCTS_COLLECTION, id);
    
    // Get the current product
    const productDoc = await getDoc(productRef);
    if (!productDoc.exists()) {
      throw new Error(`Product with ID ${id} not found`);
    }
    
    // Add updatedAt timestamp to the info object
    const updatedProduct: Record<string, any> = {
      ...updates,
      info: updates.info ? {
        ...updates.info,
        updatedAt: new Date()
      } : undefined,
      _updatedAt: serverTimestamp()
    };
    
    // Remove undefined fields
    Object.keys(updatedProduct).forEach(key => 
      updatedProduct[key] === undefined && delete updatedProduct[key]
    );
    
    // Update the document
    await updateDoc(productRef, updatedProduct);
  } catch (error) {
    handleFirestoreError(error);
    throw error;
  }
}

// Delete a product
export async function deleteProduct(id: string): Promise<void> {
  try {
    await deleteDoc(doc(db as Firestore, PRODUCTS_COLLECTION, id));
    
    // Later we could also clean up related subcollections here
    // For now, we'll leave that as a future improvement
  } catch (error) {
    handleFirestoreError(error);
    throw error;
  }
}

// Update metrics for a product
export async function updateMetrics(
  productId: string,
  updates: {
    growthMetrics?: Partial<GrowthMetrics>;
    revenueMetrics?: Partial<RevenueMetrics>;
    costMetrics?: Partial<CostMetrics>;
    customerMetrics?: Partial<CustomerMetrics>;
  }
): Promise<void> {
  try {
    const productRef = doc(db as Firestore, PRODUCTS_COLLECTION, productId);
    
    // Get the current product
    const productDoc = await getDoc(productRef);
    if (!productDoc.exists()) {
      throw new Error(`Product with ID ${productId} not found`);
    }
    
    const product = productDoc.data() as Product;
    
    // Create the update object
    const updateData: Record<string, any> = {
      _updatedAt: serverTimestamp()
    };
    
    // Only include fields that are being updated
    if (updates.growthMetrics) {
      updateData.growthMetrics = {
        ...product.growthMetrics,
        ...updates.growthMetrics
      };
    }
    
    if (updates.revenueMetrics) {
      updateData.revenueMetrics = {
        ...product.revenueMetrics,
        ...updates.revenueMetrics
      };
    }
    
    if (updates.costMetrics) {
      updateData.costMetrics = {
        ...product.costMetrics,
        ...updates.costMetrics
      };
    }
    
    if (updates.customerMetrics) {
      updateData.customerMetrics = {
        ...product.customerMetrics,
        ...updates.customerMetrics
      };
    }
    
    // Update the document
    await updateDoc(productRef, updateData);
  } catch (error) {
    handleFirestoreError(error);
    throw error;
  }
}

// Update weekly projections
export async function updateProjections(
  productId: string, 
  weeklyProjections: unknown[]
): Promise<void> {
  try {
    const productRef = doc(db as Firestore, PRODUCTS_COLLECTION, productId);
    
    // Get the current product
    const productDoc = await getDoc(productRef);
    if (!productDoc.exists()) {
      throw new Error(`Product with ID ${productId} not found`);
    }
    
    // Ensure each projection has an ID
    const updatedProjections = weeklyProjections.map(projection => ({
      ...projection,
      id: projection.id || crypto.randomUUID()
    }));
    
    // Update the document
    await updateDoc(productRef, {
      weeklyProjections: updatedProjections,
      _updatedAt: serverTimestamp()
    });
  } catch (error) {
    handleFirestoreError(error);
    throw error;
  }
}

// Update actual metrics
export async function updateActuals(
  productId: string,
  actualMetrics: unknown[]
): Promise<void> {
  try {
    const productRef = doc(db as Firestore, PRODUCTS_COLLECTION, productId);
    
    // Get the current product
    const productDoc = await getDoc(productRef);
    if (!productDoc.exists()) {
      throw new Error(`Product with ID ${productId} not found`);
    }
    
    const product = productDoc.data() as Product;
    
    // Ensure each metric has an ID
    const updatedMetrics = actualMetrics.map(metric => ({
      ...metric,
      id: metric.id || crypto.randomUUID()
    }));
    
    // Create a map of metrics by ID for easy lookup
    const metricsById = new Map();
    updatedMetrics.forEach(metric => {
      metricsById.set(metric.id, metric);
    });
    
    // Combine existing metrics (if not being updated) with updated ones
    const existingMetrics = product.actualMetrics || [];
    const combinedMetrics = existingMetrics
      .filter(metric => !metricsById.has(metric.id))
      .concat(Array.from(metricsById.values()));
    
    // Update the document
    await updateDoc(productRef, {
      actualMetrics: combinedMetrics,
      _updatedAt: serverTimestamp()
    });
  } catch (error) {
    handleFirestoreError(error);
    throw error;
  }
}

// For future enhancement: implement subcollections
// Instead of storing all data in a single document, we could use subcollections:
// - /products/{productId}/projections/{projectionId}
// - /products/{productId}/actuals/{actualId}
// This would be better for scalability with large numbers of projections or actuals 

// MARKETING KPI OPERATIONS

// Get marketing KPIs for a product
export async function getMarketingKPIs(productId: string): Promise<any[]> {
  try {
    const kpisRef = collection(db as Firestore, MARKETING_KPIS_COLLECTION);
    const q = query(kpisRef, where("productId", "==", productId));
    const snapshot = await getDocs(q);
    
    const kpis: unknown[] = [];
    snapshot.forEach(doc => {
      const kpiData = convertTimestamps(doc.data());
      kpis.push({
        ...kpiData,
        id: doc.id
      });
    });
    
    return kpis;
  } catch (error) {
    handleFirestoreError(error);
    throw error;
  }
}

// Add a new marketing KPI
export async function addMarketingKPI(productId: string, kpi: any): Promise<string> {
  try {
    // Generate a unique ID if not provided
    const kpiId = kpi.id || crypto.randomUUID();
    
    // Create the KPI with productId
    const kpiWithProductId = {
      ...kpi,
      productId,
      id: kpiId,
      createdAt: serverTimestamp(),
      updatedAt: serverTimestamp()
    };
    
    // Set the document with the generated ID
    await setDoc(doc(db as Firestore, MARKETING_KPIS_COLLECTION, kpiId), kpiWithProductId);
    
    return kpiId;
  } catch (error) {
    handleFirestoreError(error);
    throw error;
  }
}

// Update a marketing KPI
export async function updateMarketingKPI(kpiId: string, updates: any): Promise<void> {
  try {
    const kpiRef = doc(db as Firestore, MARKETING_KPIS_COLLECTION, kpiId);
    
    // Add updated timestamp
    const updatedKpi = {
      ...updates,
      updatedAt: serverTimestamp()
    };
    
    // Update the document
    await updateDoc(kpiRef, updatedKpi);
  } catch (error) {
    handleFirestoreError(error);
    throw error;
  }
}

// Delete a marketing KPI
export async function deleteMarketingKPI(kpiId: string): Promise<void> {
  try {
    await deleteDoc(doc(db as Firestore, MARKETING_KPIS_COLLECTION, kpiId));
  } catch (error) {
    handleFirestoreError(error);
    throw error;
  }
}

