        {/* Methodology */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Methodology and Definitions</Text>
          
          <View style={styles.table}>
            <View style={styles.tableRow}>
              <Text style={[styles.tableCell, { flex: 1, fontWeight: 'bold' }]}>ROI</Text>
