      const scenarios = JSON.parse(savedScenarios);
      const filteredScenarios = scenarios.filter((s: ScenarioModel) => s.id !== scenarioId);
      localStorage.setItem(SCENARIOS_KEY, JSON.stringify(filteredScenarios));
